    SalesQuotationTable::lookupOpportunityId(_formControl, _filterStr, salesQuotationTable.party());
}

public boolean validate()
{
    boolean         ret;
    ProjTable       projTable;


    ret = super();

    if (salesQuotationTable.ProjIdRef)
    {
        projTable = ProjTable::find(salesQuotationTable.ProjIdRef);

        if(ret && (projTable.Type != ProjType::TimeMaterial && projTable.Type != ProjType::FixedPrice))
        {
            ret = checkFailed("@SYS98421");
        }

        if(ret && projTable.Status == ProjStatus::Completed)
        {
            ret = checkFailed("@SYS98422");
        }

        if(ret && projTable.CustAccount != salesQuotationTable.CustAccount && salesQuotationTable.CustAccount)
        {
            ret = checkFailed("@SYS98449");
        }


        if(ret && !projTable.status().checkRule(ProjStatusRule::CreateQuotation))
        {
            ret = checkFailed(strFmt("@SYS53010", projTable.Status, projTable.ProjId));
        }
    }

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup  sysTableLookup = SysTableLookup::newParameters(tableNum(ProjTable),_formControl,true);
    QueryBuildDataSource    qbsProjTable;
    Query   query = new Query();

    sysTableLookup.addLookupfield(fieldNum(ProjTable, ProjId), true);
    sysTableLookup.addLookupfield(fieldNum(ProjTable, Name));
    sysTableLookup.addLookupfield(fieldNum(ProjTable, ProjInvoiceProjId));

    qbsProjTable = query.addDataSource(tableNum(ProjTable));
    qbsProjTable.addRange(fieldNum(ProjTable,Status)).value(strFmt('!%1',queryValue(ProjStatus::Completed)));
    qbsProjTable.addRange(fieldNum(ProjTable,Type)).value(strFmt('%1,%2',queryValue(ProjType::TimeMaterial),queryValue(ProjType::FixedPrice)));

    if(salesQuotationTable.CustAccount)
    {
        qbsProjTable.addRange(fieldNum(ProjTable,CustAccount)).value(salesQuotationTable.CustAccount);
    }

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

