public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    Common ret;

    ret = super(_formReferenceControl);

    // Extensibility TODO: Request pending at Microsoft - 390837
    if (quotationType == QuotationType::RORProjRental && ret.RecId && !ProjWorkerLookup::validateWorker(ret.RecId, salesQuotationTable))
    {
        ret.clear();
    }
    else if (!ret.RecId || !HcmWorkerLookup::newOnlyActiveWorkers().validateWorker(ret.RecId))
    {
        ret.clear();
    }

    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    if (quotationType == QuotationType::RORProjRental)
    {
        return ProjWorkerLookup::lookupWorker(_formReferenceControl, salesQuotationTable);
    }

    return HcmWorkerLookup::newOnlyActiveWorkers().lookupWorker(_formReferenceControl);
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    Common ret;

    ret = super(_formReferenceControl);

    if (quotationType == QuotationType::RORProjRental && ret.RecId && !ProjWorkerLookup::validateWorker(ret.RecId, salesQuotationTable))
    {
        ret.clear();
    }
    else if (!ret.RecId || !HcmWorkerLookup::newOnlyActiveWorkers().validateWorker(ret.RecId))
    {
        ret.clear();
    }

    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    if (quotationType == QuotationType::RORProjRental)
    {
        return ProjWorkerLookup::lookupWorker(_formReferenceControl, salesQuotationTable);
    }

    return HcmWorkerLookup::newOnlyActiveWorkers().lookupWorker(_formReferenceControl);
}

