public void modified()
{
    QuotationProjTransType  projTransType;
    ItemId                  itemId;
    ProjCategoryId          projCategoryId;
    RORTradeType            tradeType;
    RORProjTransType        rorProjTransType;

    super();

    projTransType                       = salesQuotationLine.ProjTransType;
    projCategoryId                      = salesQuotationLine.ProjCategoryId;
    itemId                              = salesQuotationLine.ItemId;
    tradeType                           = salesQuotationLine.RORTradeType;
    rorProjTransType                    = salesQuotationLine.RORProjTransType;

    salesQuotationTableForm.resetSalesQuotationLine(salesQuotationLine);

    // allow overwrite default trade type
    if (!salesQuotationLine
    &&  salesQuotationLine.RORTradeType == RORTradeType::Rental
    &&  tradeType == RORTradeType::SalesPurch)
    {
        salesQuotationLine.RORTradeType = RORTradeType::SalesPurch;
    }

    salesQuotationLine.ProjTransType    = projTransType;
    salesQuotationLine.ProjCategoryId   = projCategoryId;
    salesQuotationLine.ItemId           = itemId;
    salesQuotationLine.RORTradeType     = tradeType;
    salesQuotationLine.RORProjTransType = rorProjTransType;

    salesQuotationLine.itemIdChanged();
    salesQuotationLine_ds.updateLineFieldsMandatory();
    salesQuotationLine_ds.changedInventoriedStatus();

    // Reread ReferenceGroup controls, e.g. to show the category name.
    salesQuotationLine_ds.rereadReferenceDataSources();

    salesQuotationLine.LinePropertyId = ProjLinePropertySetup::findLinePropertyId(salesQuotationLine.salesQuotationTable().ProjIdRef,
                                                                                  salesQuotationLine.ProjCategoryId);
    salesQuotationLine.TaxItemGroup   = ProjParameters::taxItemGroupItem(salesQuotationLine.ProjCategoryId,
                                                                         salesQuotationLine.ItemId);

    inventDim.data(InventDim::find(salesQuotationLine.InventDimId));
    InventMovement::bufferSetTransQtyUnit(salesQuotationLine);

    InventTable inventTable = inventTable::find(salesQuotationLine.ItemId);
    salesQuotationLine.ProjDescription = inventTable.productName(salesQuotationTable.LanguageId, salesQuotationLine.InventDimId);
    salesQuotationLine.initName();

}

public void lookup(FormControl _formControl, str _filterStr)
{
    Args    args;
    FormRun formRun;

    args = new Args(formStr(RORRentalInventItemIdLookup));
    args.caller(element);
    args.record(salesQuotationLine);
    args.parm(_filterStr);
    formRun = classfactory.formRunClass(args);
    formRun.init();
    this.performFormLookup(formRun, _formControl);
}

