public void modified()
{
    super();

    salesQuotationLine.modifyInventDim(inventDim,fieldNum(InventDim,InventLocationId));
    if (salesQuotationLine.QuotationType != QuotationType::RORProjRental
    ||  salesQuotationLine.RORProjTransType != RORProjTransType::Rental)
    {
        salesQuotationLine.CostPrice = salesQuotationLine.inventTable().costPcsPrice(salesQuotationLine.InventDimId,inventDim);
    }
}

