public int active()
{
    int     ret;
    boolean allowEditLine;

    ret = super();

    if (salesQuotationLine.isCategoryBased())
    {
        allowEditLine = salesQuotationTableForm.editLineAllowed();
        salesQuotationLineForeignTradeCategory_ds.allowEdit(allowEditLine);
    }
    else
    {
        salesQuotationLineForeignTradeCategory_ds.allowEdit(false);
    }

    return ret;
}

