public void initValue()
{
    super();

    salesQuotationLineTaxExtensionIN.TaxModelDocLineExtensionIN::initFromBaseTable(salesQuotationLine);
    if (SalesQuotationTable::find(salesQuotationLine.QuotationId).ProjIdRef)
    {
        ProjTable projTable = ProjTable::find(salesQuotationLine.projId());
        salesQuotationLineTaxExtensionIN.TaxModelDocLineExtensionIN::copyFromSource(projTable.TableId, projTable.RecId);
    }
    else
    {
        salesQuotationLineTaxExtensionIN.TaxModelDocLineExtensionIN::init(salesQuotationLine);
    }
}

