void clicked()
{
    MenuFunction        menuFunction;
    Hierarchy           hierarchy;
    HierarchyCreate     hierarchyCreate;
    str                 hierarchyId;
    Args                args = new Args();
    boolean             isProj3;

    isProj3 = isConfigurationkeyEnabled(configurationKeyNum(Project3));

    //check if hierarchy already exists
    if (HierarchyLinkTable::findRefTableRecId(tableNum(SalesQuotationTable), salesQuotationTable.RecId) == null)
    {
        //create Hierarchy record
        hierarchyCreate = HierarchyCreate::construct(HierarchyType::Project);
        hierarchyCreate.parmIsQuotation(true);
        hierarchyCreate.parmSourceCommon(salesQuotationTable);
        hierarchyCreate.run();
        hierarchy = hierarchyCreate.parmHierarchy();

        ProjPlanVersionsManager::createDraft(hierarchy.HierarchyId, salesQuotationTable.QuotationId, salesQuotationTable.PSAEstProjStartDate, salesQuotationTable.PSAEstProjEndDate);
    }
    else
    {
        hierarchyId = HierarchyLinkTable::findRefTableRecId(tableNum(SalesQuotationTable), salesQuotationTable.RecId).HierarchyId;
        hierarchy = Hierarchy::find(hierarchyId);
    }

    args.record(hierarchy);
    args.caller(element);
    if (isProj3)
    {
        menuFunction = new MenuFunction(menuitemDisplayStr(ProjActivity), MenuItemType::Display);
    }
    else
    {
        menuFunction = new MenuFunction(menuitemDisplayStr(ProjActivityOriginal), MenuItemType::Display);
    }
    menuFunction.run(args);
}

