void clicked()
{
    super();

    // This button moves the quotation from Send state
    // In sent state, the datasource allowEdit() is set to false
    ttsBegin;
    salesQuotationTable_ds.allowEdit(true);
    salesQuotationTable.QuotationStatus = SalesQuotationStatus::Revised;
    salesQuotationTable.update();
    ttsCommit;

    element.doRefresh();
    element.editQuotation(element.editHeaderAllowed());

    // update the workflow status to allow the document to be submitted
    element.updateWorkflowControls();

}

void clicked()
{
    if (!SmmOpportunityStatusUpdate::updateFromQuote(salesQuotationTable_ds, smmOpportunityStatus::Cancelled))
    {
        super();
    }
}

void clicked()
{
    if (!SmmOpportunityStatusUpdate::updateFromQuote(salesQuotationTable_ds, smmOpportunityStatus::Lost))
    {
        super();
    }
}

void clicked()
{
    ProjQuotationId     activeQuotation;
    MenuFunction        menuFunction;
    NoYes               conversionSuccesfull;
    smmBusRelTable      smmBusRelTable = smmBusRelTable::find(salesQuotationTable.BusRelAccount);
    Args                args = new Args();

    if (smmBusRelTable.canConvertToCustomer() && Box::yesNo(strFmt("@SYS91001",salesQuotationTable.BusRelAccount),DialogButton::Yes,"@SYS81502")==DialogButton::Yes)
    {

        // Convert to customer
        conversionSuccesfull = smmBusRelTable::convert2Customer(smmBusRelTable);

        if (conversionSuccesfull)
        {
            // Save active quotation
            activeQuotation = salesQuotationTable.QuotationId;

            // Refresh the form
            salesQuotationTable_ds.reread();

            // Run through & refresh all business relations
            salesQuotationTable_ds.executeQuery();

            //  Find the original quotation you had active
            salesQuotationTable_ds.positionToRecordByValue(fieldNum(SalesQuotationTable,QuotationId),activeQuotation);

            // Open the Customer table form if configured in smmParameters to do so
            if (smmParameters::getBusRelOpenCustForm())
            {
                menuFunction = new MenuFunction(menuitemDisplayStr(CustTable), MenuItemType::Display);
                args.record(salesQuotationTable);
                menuFunction.run(args);
            }
        }
    }
}

void clicked()
{
    super();

    salesQuotationTable_ds.reread();
    salesQuotationTable_ds.refresh();
}

void clicked()
{
    element.doRefresh();
    super();
}

/// <summary>
/// Sets up the context for print management
/// </summary>
void clicked()
{
    PrintMgmtSetupContext setupContext = new PrintMgmtSetupContext();
    List                  salesDocTypes = new List(Types::Enum);

    if (salesQuotationTable_ds.cursor() == null)
    {
        return;
    }

    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(Project3)))
    {
        salesDocTypes.addEnd(PrintMgmtDocumentType::Confirmation);
        setupContext.addHierarchyContext(PrintMgmtHierarchyType::Sales, PrintMgmtNodeType::SalesQuotationTable, salesDocTypes);

        setupContext.addHierarchyContext(PrintMgmtHierarchyType::Project, PrintMgmtNodeType::ProjectQuotation);
    }
    else
    {
        setupContext.addHierarchyContext(PrintMgmtHierarchyType::Sales, PrintMgmtNodeType::SalesQuotationTable);
    }
    setupContext.parmCaller(element);
    setupContext.parmReferencedTableBuffer(salesQuotationTable_ds.cursor());
    setupContext.parmCountryRegionId(salesQuotationTable.custTable_CustAccount().postalAddress().CountryRegionId);
    PrintMgmt::launchSetup(setupContext);
}

void clicked()
{
    priceDiscTable_Sales.enabled(salesQuotationTable.CustAccount? true : false);
    salesPrice.enabled(salesQuotationTable.CustAccount? true : false);
    salesLineDisc.enabled(salesQuotationTable.CustAccount? true : false);
    salesMultiLineDisc.enabled(salesQuotationTable.CustAccount? true : false);
    salesEndDisc.enabled(salesQuotationTable.CustAccount? true : false);
    priceDiscAdmTable_SalesCreate.enabled(salesQuotationTable.CustAccount? true : false);
    SuppItemTable_Sales.enabled(salesQuotationTable.CustAccount? true : false);

    super();
}

