void lookup()
{
    ContactPerson::lookupDirContactPerson(quotation_ContactPersonId,
                                           salesQuotationTable.party(),
                                           this,
                                           salesQuotationTable.ContactPersonId);
}

public boolean modified()
{
    boolean ret;

    if (!ContactPerson::find(editContactPersonName2.text()))
    {
        salesQuotationTable.ContactPersonId = ContactPerson::findOrCreateNameParty(salesQuotationTable.party(), editContactPersonName2.text()).ContactPersonId;
    }

    ret = super();

    return ret;
}

public void jumpRef()
{
    SalesQuotationTable_RORContactPersonIdOperator.jumpRef();
}

void lookup()
{
    ContactPerson::RORLookupContactPerson(SalesQuotationTable_RORContactPersonIdOperator,
                                          salesQuotationTable,
                                          this,
                                          salesQuotationTable.RORContactPersonIdOperator,
                                          RORContactPersonRoleFilter::Operator);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (!salesQuotationTable.RORContactPersonIdOperator)
    {
        if (!ContactPerson::findNameParty(salesQuotationTable.party(), ROReditContactPersonNameOperator.text()))
        {
            salesQuotationTable.RORContactPersonIdOperator = ContactPerson::findOrCreateNameParty(salesQuotationTable.party(), ROReditContactPersonNameOperator.text()).ContactPersonId;
        }
    }

    return ret;
}

