{
    return inventDimFormSetup;
}

// Called by click() methods from lines action pane/strip
public void linePriceSimulation()
{
    RecId  callerRecordRecId;

    callerRecordRecId = salesQuotationLine.RecId;
    salesQuotationTable.updateMultiLineDisc();

    salesQuotationLine_ds.executeQuery();
    salesQuotationLine_ds.positionToRecord(SalesQuotationLine::findRecId(callerRecordRecId));
}

/// <summary>
///     Disables select fields on the form to prevent the user from closing.
/// </summary>
/// <remarks>
///     If we are leaving the form and it is required that the user
///     enter something on another form prior to additional changes on this
///     form, this method can be called to lock down the sales table form.
/// </remarks>
public void mcrDisableForm()
{
    salesQuotationTable_ds.allowEdit(false);
    salesQuotationTable_ds.allowCreate(false);
    salesQuotationTable_ds.allowDelete(false);

    headerDetailsTab.allowEdit(false);

    salesQuotationLine_ds.allowEdit(false);
    salesQuotationLine_ds.allowCreate(false);
    salesQuotationTable_ds.allowDelete(false);

    lineDetailsTabs.allowEdit(false);
}

void mcrEnableForm()
{
    salesQuotationTable_ds.allowEdit(true);
    salesQuotationTable_ds.allowCreate(true);
    salesQuotationTable_ds.allowDelete(true);

    headerDetailsTab.allowEdit(true);

    salesQuotationLine_ds.allowEdit(true);
    salesQuotationLine_ds.allowCreate(true);
    salesQuotationTable_ds.allowDelete(true);

    lineDetailsTabs.allowEdit(true);
}

void mcrHeaderPriceOverride(boolean _releaseOverride,
    RetailInformationSubcodeId _retailInformationSubcodeId,
    UserId _userId)
{

    if (_releaseOverride)
    {
        mcrSalesQuotationTable.PriceOverride = NoYes::No;
        //The mcrSalesQuotationTable.PriceOverride value is going to be needed
        //within salesTable.updateFinalDisc() which gets called below.
        mcrSalesQuotationTable.write();
        mcrHeaderPriceOverrideEventType = MCROrderEventType::SalesQuoteTablePriceOverrideTurnedOff;
        retailInformationSubcodeIdHeader = _retailInformationSubcodeId;
        mcrHeaderPriceOverrideUserId = _userId;
        mcrHeaderPriceOverrideDiscPct = salesQuotationTable.orig().DiscPercent;
        salesQuotationTable.updateFinalDisc();

        if (mcrHeaderPriceOverrideDiscPct != salesQuotationTable.DiscPercent)
        {
            element.mcrInsertPriceOverrideOrderEvent();
        }
    }
    else
    {
        mcrSalesQuotationTable.PriceOverride = NoYes::Yes;
        mcrHeaderPriceOverrideEventType = MCROrderEventType::SalesQuoteTablePriceOverrideTurnedOn;
        retailInformationSubcodeIdHeader = _retailInformationSubcodeId;
        mcrHeaderPriceOverrideUserId = _userId;
    }
}

public void mcrInsertPriceOverrideOrderEvent()
{
    MCROrderEventTable      mcrOrderEventTable;

    ttsbegin;
    mcrOrderEventTable.insertFromSalesQuoteTablePriceOverride(
        salesQuotationTable,
        mcrHeaderPriceOverrideEventType,
        retailInformationSubcodeIdHeader,
        mcrHeaderPriceOverrideUserId,
        mcrHeaderPriceOverrideDiscPct);
    ttscommit;
}

void mcrLinePriceOverrideNo(UserId _userId)
{
    //  When price override is turned off recalculate price discounts.  Also recalculate the
    //  total line amount and create a price overrided turned off order event.
    mcrSalesQuotationLineOverride.PriceOverride = NoYes::No;
    mcrSalesQuotationLineOverride.ListPrice = 0;
    mcrSalesQuotationLineOverride.ListPriceCurrencyCode = "";
    mcrSalesQuotationLineOverride.ReasonCode = "";
    salesQuotationLine.setPriceDisc(salesQuotationLine.inventDim());
    salesQuotationLine.calcLineAmountForced();
    mcrLinePriceOverrideEventType = MCROrderEventType::SalesQuoteLinePriceOverrideTurnedOn;
    // Load these values so that the appropriate values go into the order event.
    retailInformationSubcodeIdLine = "";
    retailInfocodeIdLine = "";
    mcrLinePriceOverrideUserId = _userId;
}

void mcrLinePriceOverrideYes(RetailInformationSubcodeId _retailInformationSubcodeIdLine, UserId _userId = curUserId())
{
    mcrSalesQuotationLineOverride.PriceOverride = NoYes::Yes;
    mcrSalesQuotationLineOverride.ReasonCode = _retailInformationSubcodeIdLine;
    mcrSalesQuotationLineOverride.RetailInfocodeId = RetailMCRChannelTable::findForCurrentUser().mcrPriceOverrideRetailInfocodeId;
    mcrLinePriceOverrideEventType = MCROrderEventType::SalesQuoteLinePriceOverrideTurnedOn;
    // Load these values so that the appropriate values go into the order event.
    retailInformationSubcodeIdLine = _retailInformationSubcodeIdLine;
    retailInfocodeIdLine = mcrSalesQuotationLineOverride.RetailInfocodeId;
    mcrLinePriceOverrideUserId = _userId;
}

/// <summary>
/// Refreshes the estimated margin percentage and margin icon if the margin alert
/// is enabled.
/// </summary>
void mcrRefreshMarginAlert()
{
    if (salesParameters.mcrEnableMarginAlert)
    {
        marginMap.remove(salesQuotationLine.InventTransId);
    }
}

/// <summary>
///     Enables or disables editing of the item and sales category fields.
/// </summary>
/// <param name="allowEdit">
///     Whether to enable editing.
/// </param>
void mcrSetEditItemOrCategoryAllowed(boolean  allowEdit)
{
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, ItemId)).allowEdit(allowEdit);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, SalesCategory)).allowEdit(allowEdit);
}

void mcrSetLineControls()
{
    boolean allowEdit = true;

    //  Allow and disallow editing of controls/datasources as appropriate.
    if (salesQuotationLine.RecId)
    {
        salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, ItemId)).allowEdit(false);
    }
    else
    {
        salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, ItemId)).allowEdit(true);
    }

    allowEdit =  salesQuotationTable.checkUpdate(false);

    salesQuotationLine_ds.allowEdit(allowEdit);

    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, SalesUnit)).allowEdit(
        !salesQuotationLine.mcrSalesQuotationLineOverride().PriceOverride
        && (salesQuotationLine.SalesQty == salesQuotationLine.RemainSalesPhysical
        || !salesQuotationLine.RecId));

    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, ItemId)).allowEdit(!salesQuotationLine.RecId);

    markupTrans_LineQuotation.enabled(allowEdit);

}

void mcrUpdateSalesQuotationLine()
{
    ItemId              itemId;

    itemId              = salesQuotationLine.ItemId;

    salesQuotationTableForm.resetSalesQuotationLine(salesQuotationLine);

    salesQuotationLine.ItemId    = itemId;

    salesQuotationLine.itemIdChanged();
    inventDim.data(InventDim::find(salesQuotationLine.InventDimId));

    InventMovement::bufferSetTransQtyUnit(salesQuotationLine);

    element.updateDesign(InventDimFormDesignUpdate::FieldChange);

    salesQuotationLine_ds.mcrFormInitSourceCode();
}

/// <summary>
/// Calculates multi line discounts.
/// </summary>
public void multLineDiscount()
{
    salesQuotationTable.updateMultiLineDisc();
    salesQuotationLine_ds.research(true);
}

