/// <summary>
/// Enables or disables editing of the sales category field.
/// </summary>
/// <param name="_allowEdit">
/// Whether to enable editing.
/// </param>
/// <remarks>
/// In a Brazilian context, editing of the sales category field must always be disabled.
/// </remarks>
void setEditCategoryAllowed(boolean _allowEdit)
{
    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        _allowEdit = false;
    }
    // </GBR>

    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, SalesCategory)).allowEdit(_allowEdit);
}

public void setFieldAccessHeader()
{
    boolean     showTemplateFields  = true;
    boolean     isCustomer;

    isCustomer = salesQuotationTable.CustAccount ? true : false;

    if (crmLicense)
    {
        // Enable/disable discount group if Quotation is for a customer/prospect
        element.checkEnableDiscount(isCustomer);
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, BusRelAccount)).allowEdit(salesQuotationTable.QuotationStatus != SalesQuotationStatus::Sent);
    }

    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, InvoiceAccount)).allowEdit(isCustomer);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, vatNum)).allowEdit(isCustomer);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, CustAccount)).allowEdit(salesQuotationTable.QuotationStatus != SalesQuotationStatus::Sent);

    //  template fields can not be changed if status <> created or if quotation lines exist
    if ((salesQuotationTable.QuotationStatus != SalesQuotationStatus::Created) ||
        SalesQuotationLine::exist(salesQuotationTable.QuotationId))
        showTemplateFields  = false;

    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, TemplateActive)).allowEdit(salesQuotationTable.TemplateName ? true : false);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, TemplateGroupId)).allowEdit(showTemplateFields);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, TemplateName)).allowEdit(showTemplateFields);
}

public void setFieldAccessLine()
{
    boolean inventoriedItem = salesQuotationLine.isStocked();
    boolean itemBased       = salesQuotationLine.ItemId != '';
    boolean isCWItem        = PdsGlobal::pdsIsCWItem(salesQuotationLine.ItemId);

    // Multi line discount is not supported for prospects
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, MultiLnDisc)).allowEdit(salesQuotationTable.CustAccount ? true : false);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, MultiLnPercent)).allowEdit(salesQuotationTable.CustAccount ? true : false);
    multiLnDiscAction.enabled(salesQuotationTable.CustAccount != '');

    // Disable inventory fields if not inventoried
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, QtyOrdered)).allowEdit(inventoriedItem);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, InventDeliverNow)).allowEdit(inventoriedItem);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, RemainInventPhysical)).allowEdit(inventoriedItem);
    if (#PdsEnabled)
    {
        enableDSField(salesQuotationLine_ds, fieldNum(SalesQuotationLine, QtyOrdered), inventoriedItem && !isCWItem);
        enableDSField(salesQuotationLine_ds, fieldNum(SalesQuotationLine, InventDeliverNow), inventoriedItem && !isCWItem);
        enableDSField(salesQuotationLine_ds, fieldNum(SalesQuotationLine, RemainInventPhysical), inventoriedItem && !isCWItem);
    }
    else
    {
        salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, QtyOrdered)).allowEdit(inventoriedItem);
        salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, InventDeliverNow)).allowEdit(inventoriedItem);
        salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, RemainInventPhysical)).allowEdit(inventoriedItem);
    }
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, CostPrice)).allowEdit(inventoriedItem);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, OrigCostPrice)).allowEdit(inventoriedItem);
    element.enableLineInquiryActions(inventoriedItem);

    // Disable delivery fields if not item based
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, BarCode)).allowEdit(itemBased);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, BarCodeType)).allowEdit(itemBased);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, ItemRouteId)).allowEdit(itemBased);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, ItemBOMId)).allowEdit(itemBased);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, LineHeader)).allowEdit(itemBased);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, PriceCalcId)).allowEdit(itemBased);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, NewTotalContributionRatio)).allowEdit(itemBased);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, NewSalesPrice)).allowEdit(itemBased);
}

