{
    element.design().caption("@DNR6280");

    headerTitle.text(strFmt('%1 : %2 - %3', salesQuotationTable.QuotationId,
                                            salesQuotationTable.CustAccount ? salesQuotationTable.CustAccount : salesQuotationTable.BusRelAccount,
                                            salesQuotationTable.CustAccount ? salesQuotationTable.customerName() : salesQuotationTable.busRelName()));

}

