    /// <summary>
    /// Record details of manual override.
    /// </summary>
    /// <param name="_retailInformationSubcodeIdLine">
    /// Record from <c>RetailInformationSubcodeId</c> table.
    /// </param>
    /// <param name="_userId">
    /// user id.
    /// </param>
    void mcrLineManualDiscountOverride(RetailInformationSubcodeId _retailInformationSubcodeIdLine, UserId _userId = curUserId())
    {
        mcrSalesQuotationLineOverride.PriceOverride = NoYes::No;
        mcrSalesQuotationLineOverride.ReasonCode = _retailInformationSubcodeIdLine;
        mcrSalesQuotationLineOverride.RetailInfocodeId = RetailMCRChannelTable::findForCurrentUser().mcrPriceOverrideRetailInfocodeId;
        mcrLinePriceOverrideEventType = MCROrderEventType::SalesQuoteLinePriceOverrideTurnedOn;
        // Load these values so that the appropriate values go into the order event.
        retailInformationSubcodeIdLine = _retailInformationSubcodeIdLine;
        retailInfocodeIdLine = mcrSalesQuotationLineOverride.RetailInfocodeId;
        mcrLinePriceOverrideUserId = _userId;
    }

