    salesQuotationTableForm.setTouched(salesQuotationTable);
}

boolean validateWrite()
{
    boolean         ret;
    SalesCalcAvailableDlvDates salesCalcAvailableDlvDates;

    custParameters = CustParameters::find();
    ret = super();

    if (ret && salesQuotationTableType && !salesQuotationTableType.formMethodDataSourceValidateWrite(element, salesQuotationTable_ds))
    {
        ret = false;
    }

    if (ret)
    {
        salesCalcAvailableDlvDates = SalesCalcAvailableDlvDates::newCommonSalesDlvDateType(salesQuotationTable);
        ret = salesCalcAvailableDlvDates.validateWritePrompt(salesQuotationTable);
    }

    copyTaxGroupToMarkupTrans   = DialogButton::No;
    if (ret && salesQuotationTable.TaxGroup != salesQuotationTable.orig().TaxGroup)
    {
        copyTaxGroupToMarkupTrans   = MarkupTrans::promptCopyTaxGroupToMarkupTrans(salesQuotationTable.TableId, salesQuotationTable.RecId);
        ret                         = copyTaxGroupToMarkupTrans != DialogButton::Cancel;
    }

    if (ret)
    {
        salesQuotationToLineUpdatePrompt = SalesQuotationToLineUpdatePrompt::newExecutePrompt(salesQuotationTable, copyTaxGroupToMarkupTrans);
        // If the salesQuotationToLineUpdatePrompt object is null no fields are to be updated.
        if(salesQuotationToLineUpdatePrompt)
        {
            ret = salesQuotationToLineUpdatePrompt.parmExtendedPromptReturnValue();
        }
    }

    return ret;
}

void  write()
{
    #OCCRetryCount
    DialogButton            copyAddress;
    boolean                 convertCurrency         = false;
    boolean                 linesExist              = false;
    TradeCurencyConversion  tradeCurencyConversion  = TradeCurencyConversion::ExchRate;
    smmActivities           smmActivities;
    MCRSourceCode           sourceCode;
    SalesQuotationTable     salesQuotationTableLocal;
    SalesQuotationTable     salesQuotationTableOrig;

    salesQuotationTableOrig.data(SalesQuotationTable.orig());

    if (salesQuotationTable.checkUpdate())
    {
        linesExist = SalesQuotationLine::exist(salesQuotationTable.QuotationId);
        if (linesExist || MarkupTrans::exist(salesQuotationTable.TableId, salesQuotationTable.RecId))
        {
            if (salesQuotationTable.CurrencyCode != salesQuotationTable.orig().CurrencyCode)
            {
                [convertCurrency, tradeCurencyConversion] = SalesQuotationTable::promptConvertCurrencyCode(salesQuotationTable);
            }
        }

        if (linesExist)
        {
            // Extensibility TODO: Request pending at Microsoft - 390419
            if (salesQuotationTable.RORVenueCode != salesQuotationTable.orig().RORVenueCode)
            {
                salesQuotationTable.RORinitFromVenueTable(RORVenueTable::find(salesQuotationTable.RORVenueCode));
            }

            if (salesQuotationTable.DeliveryPostalAddress != salesQuotationTable.orig().DeliveryPostalAddress)
            {
                copyAddress = SalesQuotationTable::promptCopyDeliveryAddress();
            }
        }

        if (salesQuotationTable.BusRelAccount)
        {
            salesQuotationTable.updateFromAdvPromptActivity(salesQuotationTable.orig());
        }

        try
        {
            ttsbegin;

            if (copyAddress != DialogButton::Cancel  &&  copyAddress != DialogButton::No)
            {
                salesQuotationTable.copyAddressToLine(copyAddress, salesQuotationTable);
            }

            if (convertCurrency)
            {
                salesQuotationTable.convertCurrencyCode(salesQuotationTable.CurrencyCode, tradeCurencyConversion);
            }

            salesQuotationTable.Touched = NoYes::No;

            if (salesQuotationTableType)
            {
                salesQuotationTableType.formMethodDataSourceWrite(element, salesQuotationTable_ds);
            }

            if (copyTaxGroupToMarkupTrans == DialogButton::Yes || copyTaxGroupToMarkupTrans == DialogButton::YesToAll)
            {
                MarkupTrans::updateTaxGroup(salesQuotationTable.TableId, salesQuotationTable.RecId, salesQuotationTable.TaxGroup, salesQuotationTable.orig().TaxGroup, copyTaxGroupToMarkupTrans);
                copyTaxGroupToMarkupTrans = DialogButton::No;
            }

            //Save off the current value of DiscPercent in case it changes.
            //If it does change an order event will be written after the super().
            mcrHeaderPriceOverrideDiscPct = salesQuotationTable.orig().DiscPercent;

            super();

            // Apply all defaults and create free gift lines from the source code.  Must happen
            // after insert, because we are adding a line.
            if (isConfigurationkeyEnabled(configurationKeyNum(MCRSourceCode)))
            {
                if (mcrSalesQuotationTable.SourceId)
                {
                    salesQuotationTableLocal    = SalesQuotationTable::find(salesQuotationTable.QuotationId, true);
                    sourceCode                  = MCRSourceCode::construct(salesQuotationTable);
                    salesQuotationTableLocal    = sourceCode.mcrInitFromSourceCodeSetup(MCRSourceCodeSetup::find(mcrSalesQuotationTable.SourceId),
                                                                                salesQuotationTable.CustAccount);
                    if (salesQuotationTableLocal.validateWrite())
                    {
                        salesQuotationTableLocal.update();
                    }
                    else
                    {
                        throw error("@SYS104318");
                    }
                }
            }

            // Insert total discount override if occurred
            if (mcrHeaderPriceOverrideUserId
                && salesQuotationTable.DiscPercent != mcrHeaderPriceOverrideDiscPct)
            {
                element.mcrInsertPriceOverrideOrderEvent();
                mcrHeaderPriceOverrideDiscPct = salesQuotationTable.DiscPercent;
            }

            if (isRetailOrder && retailOrderNeedRecalculation)
            {
                element.recalculateRetailPricesDiscounts();
            }

            if (salesQuotationToLineUpdatePrompt)
            {
                salesQuotationToLineUpdatePrompt.runOperation();
                if (salesQuotationToLineUpdatePrompt.wasLineUpdated())
                {
                    salesQuotationLine_DS.research();
                }
                salesQuotationToLineUpdatePrompt = null;
            }

            ttscommit;
        }
        catch (Exception::Deadlock)
        {
            retry;
        }
        catch (Exception::UpdateConflict)
        {
            if (appl.ttsLevel() == 0)
            {
                if (xSession::currentRetryCount() >= #RetryNum)
                {
                    throw Exception::UpdateConflictNotRecovered;
                }
                else
                {
                    retry;
                }
            }
            else
            {
                throw Exception::UpdateConflict;
            }
        }
        catch (Exception::Error)
        {
            if (salesQuotationTable.QuotationFollowupActivity
                && salesQuotationTable.orig().QuotationFollowupActivity != salesQuotationTable.QuotationFollowupActivity)
            {
                ttsbegin;
                salesQuotationTable.QuotationFollowupActivity = salesQuotationTable.orig().QuotationFollowupActivity;
                smmActivities = smmActivities::find(salesQuotationTable.QuotationFollowupActivity,true);
                if (smmActivities)
                {
                    smmActivities.delete(false);
                }
                ttscommit;
            }
        }
        salesQuotationTableType = null;
    }
}

/// <summary>
/// Makes manual discount percentage field visible and disable <c>SalesQuotationLine</c> dicount fields.
/// </summary>
/// <param name="_enableDisableDiscountFields">
/// The flag to enable disable specific fields.
/// </param>
/// <remarks>
/// Use for retail quote.
/// </remarks>
private void enableRetailFields(boolean _enableDisableDiscountFields)
{
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, LineDisc)).enabled(!_enableDisableDiscountFields);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, LinePercent)).enabled(!_enableDisableDiscountFields);

    retailSalesQuotationLine_ds.object(fieldNum(RetailSalesQuotationLine, LineManualDiscountPercentage)).enabled(_enableDisableDiscountFields);
}

