public void modified()
{
    smmQuotationAlternativeQuotations   smmQuotationAlternativeQuotations;
    boolean                             isOrigCustAccount = false;

    if (crmLicense)
    {
        if (smmQuotationAlternativeQuotations::exist(salesQuotationTable.QuotationId))
        {
            if (Box::yesNo("@SYS90096" + " " + "@SYS41157", DialogButton::No, "@SYS23828", "@SYS23828") == DialogButton::Yes)
            {
                ttsbegin;

                delete_from smmQuotationAlternativeQuotations where smmQuotationAlternativeQuotations.QuotationId == salesQuotationTable.QuotationId;

                ttscommit;
            }
            else
            {
                salesQuotationTable.CustAccount     = salesQuotationTable.orig().CustAccount;
                isOrigCustAccount                   = true;
            }
        }
        else
        if (smmQuotationAlternativeQuotations::existLinked(salesQuotationTable.QuotationId))
        {
            if (Box::yesNo("@SYS90096" + " " + "@SYS41157",DialogButton::No, "@SYS23828", "@SYS23828") == DialogButton::Yes)
            {
                ttsbegin;

                select forupdate smmQuotationAlternativeQuotations
                    where smmQuotationAlternativeQuotations.LinkedQuotationId == salesQuotationTable.QuotationId;
                smmQuotationAlternativeQuotations.delete();

                ttscommit;
            }
            else
            {
                salesQuotationTable.CustAccount = salesQuotationTable.orig().CustAccount;
                isOrigCustAccount               = true;
            }
        }

        if (salesQuotationTable.CustAccount != '' && isOrigCustAccount == false)
        {
            super();
            element.setFieldAccessHeader();
            salesQuotationTable.transferCustAccount();
        }
        else if (isOrigCustAccount == true)
        {
            salesQuotationTable.InvoiceAccount = salesQuotationTable.orig().InvoiceAccount;
        }

    }
    else
    {
        super();
        element.setFieldAccessHeader();
        salesQuotationTable.transferCustAccount();
    }

    salesQuotationTable_ds.rereadReferenceDataSources();
}

