public boolean validate()
{
    boolean             ret;

    ret = super();

    if(ret && salesQuotationTable.isTemplate())
    {
        // Template name must be unique in a group
        if (SalesQuotationTable::existTemplate(salesQuotationTable.QuotationType,salesQuotationTable.TemplateGroupId, salesQuotationTable.TemplateName))
        {
            // Template name '%1' already exists in template group %2.
            ret = checkFailed(strFmt("@SYS99924", salesQuotationTable.TemplateName, salesQuotationTable.TemplateGroupId));
        }
    }

    return ret;
}

