}

private void updateRetailDiscounts()
{
    RetailSalesQuotationTable retailSalesQuotation;
    RetailSalesQuotationLine retailSalesQuotationLineLocal;
    RetailSalesQuotationDiscountLine discountLine;
    container retailCalculatorResultContainer;
    RetailSalesOrderCalculatorResult result = new RetailSalesOrderCalculatorResult();
    ListEnumerator discountLineEn;

    if (!RetailParameters::isRetailEnabledAndInUse())
    {
        return;
    }

    retailSalesQuotation = RetailSalesQuotationTable::find(salesQuotationTable.QuotationId);
    if (retailSalesQuotation && retailSalesQuotation.RetailChannel)
    {
        retailCalculatorResultContainer = retailSalesQuotation.TmpPriceDiscountData;
        // If the result is empty, do nothing. MCR may result in calling write() twice.
        if (retailCalculatorResultContainer)
        {
            result.unpack(retailCalculatorResultContainer);

            if(result.parmNeedsRecalculateWholeOrder() && Box::yesNo("@REX4161136", DialogButton::Yes, "@REX4520329") == DialogButton::Yes)
            {
                element.recalculateRetailPricesDiscounts();
            }
            else
            {
                ttsbegin;

                retailSalesQuotationLineLocal = RetailSalesQuotationLine::findBySalesQuotationLine(salesQuotationLine.RecId, true);

                retailSalesQuotationLineLocal.LineDscAmount = result.parmRetailSalesQuotationLine().LineDscAmount;
                retailSalesQuotationLineLocal.LineManualDiscountAmount = result.parmRetailSalesQuotationLine().LineManualDiscountAmount;
                retailSalesQuotationLineLocal.LineManualDiscountPercentage = result.parmRetailSalesQuotationLine().LineManualDiscountPercentage;
                retailSalesQuotationLineLocal.PeriodicDiscount = result.parmRetailSalesQuotationLine().PeriodicDiscount;
                retailSalesQuotationLineLocal.PeriodicPercentageDiscount = result.parmRetailSalesQuotationLine().PeriodicPercentageDiscount;
                retailSalesQuotationLineLocal.TotalDiscount = result.parmRetailSalesQuotationLine().TotalDiscount;
                retailSalesQuotationLineLocal.TotalPctDiscount = result.parmRetailSalesQuotationLine().TotalPctDiscount;

                retailSalesQuotationLineLocal.update();

                delete_from discountLine
                        where discountLine.InventTransId == salesQuotationLine.InventTransId;

                discountLineEn = result.getRetailSalesDiscountLineList().getEnumerator();

                while (discountLineEn.moveNext())
                {
                    discountLine = discountLineEn.current();
                    if (!discountLine.InventTransId)
                    {
                        discountLine.InventTransId = salesQuotationLine.InventTransId;
                    }

                    if (discountLine.InventTransId == salesQuotationLine.InventTransId)
                    {
                        discountLine.insert();
                    }
                }

                retailSalesQuotation = RetailSalesQuotationTable::find(salesQuotationTable.QuotationId, true);
                retailSalesQuotation.TmpPriceDiscountData = conNull();
                retailSalesQuotation.update();

                ttscommit;
            }
        }
    }
}

public void displayOption(Common _record, FormRowDisplayOption _options)
{
    SalesQuotationLine salesQuotationLineLocal;
    int                priceOverrideLineColor;

    _options.clearBackColor();

    // if using price control and price was overridden on this line, set the Price Override Line Color
    // from the sales parameter table.
    if (enableOrderPriceControl)
    {
        salesQuotationLineLocal = _record;
        priceOverrideLineColor  = mcrOrderParameters.mcrPriceOverrideLineColor;

        if (priceOverrideLineColor
            && salesQuotationLineLocal.mcrSalesQuotationLineOverride().PriceOverride == NoYes::Yes)
        {
            _options.backColor(priceOverrideLineColor);
        }
    }

    super(_record, _options);
}

int  active()
{
    int         ret;
    boolean     allowEditRentalPricing;
    boolean     allowEditSalesPricing;
    boolean     allowEditQty;

    ret = super();

    if (enableDirectedSelling)
    {
        element.mcrSetLineControls();
        element.mcrSetEditItemOrCategoryAllowed(salesQuotationLine.RecId == 0);
    }

    if (enableOrderPriceControl)
    {
        mcrLinePriceOverrideUserId = "";
        retailInformationSubcodeIdLine = "";
        retailInfocodeIdLine = "";
    }
    salesQuotationTableForm.salesQuotationTable(salesQuotationTable);

    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, ItemId)).allowEdit(salesQuotationLine.RecId == 0);
    element.setEditCategoryAllowed(salesQuotationLine.RecId == 0);

    [allowEditRentalPricing, allowEditSalesPricing, allowEditQty] = salesQuotationLine.RORRentalLine::CheckAllowEdit();
    this.updateDesign(allowEditRentalPricing, allowEditSalesPricing, allowEditQty);

    salesQuotationLine_ds.allowCreate   (salesQuotationTableForm.createLineAllowed(salesQuotationLine));
    salesQuotationLine_ds.allowEdit     (salesQuotationTableForm.editLineAllowed());
    salesQuotationLine_ds.allowDelete   (salesQuotationTableForm.deleteLineAllowed(salesQuotationLine));

    element.updateDesign(InventDimFormDesignUpdate::Active);

    salesQuotationTableForm.setLineAccess(salesQuotationLine);

    element.setFieldAccessLine();
    element.enableHeaderUpdateActions();
    element.showInactiveAddressBar();

    formCtrl.dataSourceActivePost();

    this.updateLineButtonsDesign();
    this.updateLineFieldsMandatory();

    this.BOBUpdateDesign();
    element.RORUpdateDesign();

    return ret;
}

public void init()
{
    super();

    // Cache display methods for performance
    this.cacheAddMethod(tableMethodStr(SalesQuotationLine, itemName));

    formCtrl.dataSourceInitPost(salesQuotationLine);

    logisticsPostalAddressLine_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);
    logisticsPostalAddressLine_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(),
                                                                      DateTimeUtil::maxValue());
}

void  initValue()
{
    LineNum generatedLineNum;
    if (salesQuotationTable
       && (defaultSalesQuotationLine.QuotationId != salesQuotationTable.QuotationId
       || (defaultSalesQuotationLine.QuotationId == salesQuotationTable.QuotationId
       && salesQuotationTableRecVersion          != salesQuotationTable.RecVersion)))
    {
        defaultSalesQuotationLine.clear();
        defaultSalesQuotationLine.initFromSalesQuotationTable(salesQuotationTable);
        salesQuotationTableRecVersion = salesQuotationTable.RecVersion;
    }

    generatedLineNum = salesQuotationLine.LineNum;
    salesQuotationLine.data(defaultSalesQuotationLine);
    salesQuotationLine.LineNum = generatedLineNum;

    // For category based lines Name should be kept if manually modified.
    keepName = false;

    super();
}

/// <summary>
/// Updates the form to reflect the current inventoried status inventoried status, i.e. after a change to the itemid, category, or inventoried field.
/// </summary>
void changedInventoriedStatus()
{
    // Update inventDim with new data which has been set due to modification
    inventDim.data(InventDim::find(salesQuotationLine.InventDimId));
    salesQuotationLine.setDeliveryDateControlType(salesQuotationLine.InventDimId, salesQuotationTable);

    InventMovement::bufferSetTransQtyUnit(salesQuotationLine);
    // Update inventdim fields
    element.updateDesign(InventDimFormDesignUpdate::FieldChange);
    // Update tagging fields based on dimension settings.

    // Disable category editing if itemid is selected
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, SalesCategory)).allowEdit(salesQuotationLine.ItemId == '');
    element.setEditCategoryAllowed(salesQuotationLine.ItemId == '');

    // Disable itemid editing if only a category is selected
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, ItemId)).allowEdit(!salesQuotationLine.isCategoryBased());

    // Update allow edit for inventory fields
    element.setFieldAccessLine();
    salesQuotationTableForm.setLineAccess(salesQuotationLine);
}

void  create(boolean  append = true)
{
    element.mcrSetEditItemOrCategoryAllowed(true);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, ItemId)).allowEdit(true);
    element.setEditCategoryAllowed(true);

    if (!salesQuotationTableForm.canNewLineBeAppended(salesQuotationLine))
    {
        append = false;
    }

    if (salesQuotationTable.checkUpdate())
    {
        super(append);
    }

    salesQuotationLine_ds.refresh();

    salesQuotationTableForm.salesQuotationLine_Create(salesQuotationLine);
}

void  delete()
{
    boolean             mustResearch;
    SalesQuotationLine  salesQuotationLinePreDelete;

    salesQuotationLinePreDelete.data(SalesQuotationLine.data());

    if (salesQuotationLine.checkDelete())
    {
        SalesQuotationId    quotationId = salesQuotationLine.QuotationId;
        RefRecId            recIdParent = salesQuotationLine.RORParentLineRecId;

        // Current price references may need to be deleted.
        salesQuotationLine.salesPurchLineInterface().cleanUpPriceHistoryRef();

        mustResearch = (SalesQuotationLine.RecId == this.getLastSelectedQuoteLineRecId());

        if (salesQuotationLine.RecId && salesQuotationLine.RORhasChildren(false, false) && salesQuotationLine_ds.validateDelete())
        {
            salesQuotationLine.type().RORDeleteChildren();
        }

        super();

        // if retail order, we need to recalculate after deleting a line
        if (RetailParameters::isRetailEnabledAndInUse() &&
            RetailPricingEngine::useRetailPricing(salesQuotationline))
        {
            RetailSalesQuotationTable   retailQuotationTable    = salesQuotationTable.retailSalesQuotationTable();
            RetailOrderCalculator       retailCalculator        = RetailOrderCalculator::construct(tableNum(SalesQuotationTable), salesQuotationTable.QuotationId);

            retailCalculator.setPricesDiscountsOnOrder(retailQuotationTable.TotalManualDiscountPercentage);

            salesQuotationLine_ds.research(true);
        }

        if (salesQuotationTable.QuotationType == QuotationType::RORRental)
        {
            salesQuotationLinePreDelete.RORRentalLine::updateContractWithChanges();
        }

        // Update kit pricing if the deleted line was part of a kit.
        if (salesQuotationLine.RecId && recIdParent)
        {
            RORKitPriceManager::construct(SalesQuotationLine::findRecId(recIdParent, true), RORKitPriceUpdateType::FromKit, RORKitPricingActionType::DeleteLine).run();
        }

        if (quotationId && SalesQuotationTable::exist(quotationId))
        {
            salesQuotationTable_ds.reread();
            salesQuotationTable_ds.refresh();
        }

        element.setFieldAccessHeader();

        if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
        {
            if (!this.mcrMoreRecordsMarked())
            {
                salesQuotationLine_ds.executeQuery();
            }
        }

        if (mustResearch)
        {
            SalesQuotationLine_ds.research();

            lastSelectedQuoteLineRecId = 0;
        }
    }
}

void mcrFormInitSourceCode()
{
    MCRSourceCodeLine mcrSourceCodeLine;

    if (isConfigurationkeyEnabled(configurationKeyNum(MCRSourceCode)))
    {
        if (mcrSalesQuotationLine.SourceId)
        {
            mcrSourceCodeLine = MCRSourceCodeLine::construct(salesQuotationLine);
            mcrSourceCodeLine.mcrInitFromSourceCodeSetup(MCRSourceCodeSetup::find(mcrSalesQuotationLine.SourceId));
        }
    }
}

public void executeQuery()
{
    super();

    if (isCalledFromLP)
    {
        isCalledFromLP = false;

        if (salesQuotationTable && TradeFormHelper::isEditableNotGridView(element.args()))
        {
            salesQuotationLine_SalesQty.setFocus();
        }
    }
}

/// <summary>
/// Prompts the user for a reason code when overriding price fields.
/// </summary>
/// <param name="_fieldId">
/// The ID of the field being overridden.
/// </param>
/// <param name="_newValue">
/// The new value the user entered.
/// </param>
/// <param name="_overrideFieldId">
/// The ID of the parameter field used to check whether or not the
/// current user has permission.
/// </param>
/// <returns>
/// true if the value can be changed; otherwise, false.
/// </returns>
public boolean mcrGetOverrideResponse(
    FieldId             _fieldId,
    real                _newValue,
    FieldId             _overrideFieldId)
{
    MCROverrideLogin_OverridePrice  mcrOverrideLogin_OverridePrice;
    boolean                         currentUserPermission;
    boolean                         cancelOverride;
    Args                            args;
    container                       userIdReasonCode;
    RetailInformationSubcodeId      retailInformationSubcodeId;
    MCRMarginAlert_Quotation        mcrMarginAlert_Quotation;
    AmountCur                       brokerages;
    AmountCur                       rebates;
    AmountCur                       royalties;
    AmountCur                       totalDeductible;
    real                            marginPrice;

    if (enableOrderPriceControl)
    {
        mcrOverrideLogin_OverridePrice = new MCROverrideLogin_OverridePrice();

        // Convert to a percentage based on item cost.
        if (_overrideFieldId == fieldNum(MCRPriceOverrideMarkUpGroup, CostMarkUpPercent))
        {
            mcrMarginAlert_Quotation = MCRMarginAlert_Quotation::newSalesQuotationLine(salesQuotationLine);
            mcrMarginAlert_Quotation.calcMargin();
            brokerages      = mcrMarginAlert_Quotation.parmBrokerAmountPerUnit();
            rebates         = mcrMarginAlert_Quotation.parmCustRebateAmountPerUnit();
            royalties       = mcrMarginAlert_Quotation.parmRoyaltyAmountPerUnit();
            totalDeductible = brokerages + rebates + royalties;

            _newValue = 100 * mcrOverrideLogin_OverridePrice.calculateCostMarkupPercent(
                salesQuotationLine.ItemId,
                salesQuotationLine.InventDimId,
                salesQuotationLine.SalesUnit,
                salesQuotationLine.mcrPricePerSalesQty());
            marginPrice = 100 * mcrOverrideLogin_OverridePrice.calculateMarginCostMarkupPercent(
                salesQuotationLine.ItemId,
                salesQuotationLine.InventDimId,
                salesQuotationLine.SalesUnit,
                salesQuotationLine.mcrPricePerSalesQty(),
                totalDeductible);
        }

        // See if the current user has permission to override the price.  If they do not,
        // call the form for a different user to log in.
        currentUserPermission = mcrOverrideLogin_OverridePrice.isUserValid(
            curUserId(),
            _newValue,
            _overrideFieldId,
            marginPrice);

        // Do not allow the user to manipulate the form while a dialog is open.
        element.mcrDisableForm();

        // If the current user does not have permission
        if (!currentUserPermission)
        {
            args = mcrOverrideLogin_OverridePrice.launchPriceOverrideLogin(
                salesQuotationLine,
                NoYes::Yes,
                _fieldId);

            // If the user did not cancel out or close using the X without first getting a valid login
            // from the override form it means that someone had a successful override
            if (args.parm() != #Cancel)
            {
                // the args.parm passes out user id and reason code
                userIdReasonCode = str2con(args.parm());
                element.mcrLinePriceOverrideYes(
                    conPeek(userIdReasonCode, 2),
                    conPeek(userIdReasonCode, 1));

                if (_fieldId == fieldNum(SalesQuotationLine, SalesPrice))
                {
                    salesQuotationLine_ds.refresh();
                }
            }
            else
            {
                cancelOverride = true;
            }
        }
        else
        {
            // If current user can override the price.
            retailInformationSubcodeId = mcrOverrideLogin_OverridePrice.launchPriceOverrideReasonCode(salesQuotationLine, NoYes::Yes);
            if (retailInformationSubcodeId != #Cancel)
            {
                if (_fieldId == fieldNum(RetailSalesQuotationLine, LineManualDiscountPercentage))
                {
                    element.mcrLineManualDiscountOverride(retailInformationSubcodeId, curUserId());
                }
                else
                {
                    element.mcrLinePriceOverrideYes(retailInformationSubcodeId, curUserId());
                }

                if (_fieldId == fieldNum(SalesQuotationLine, SalesPrice))
                {
                    salesQuotationLine_ds.refresh();
                }
            }
            else
            {
                cancelOverride = true;
            }
        }
        if (cancelOverride && !mcrSalesQuotationLineOverride.RecId)
        {
            mcrSalesQuotationLineOverride.ListPrice = (salesQuotationLine.LineAmount / (salesQuotationLine.SalesQty != 0 ? salesQuotationLine.SalesQty : 1));
            mcrSalesQuotationLineOverride.ListPriceCurrencyCode = salesQuotationLine.CurrencyCode;
        }

        element.mcrEnableForm();

        element.mcrSetLineControls();
    }

    // Return false if it was cancelled, otehrwise return true
    return !cancelOverride;
}

boolean mcrMoreRecordsMarked()
{
    SalesQuotationLine salesQuotationLineLocal;

    for (salesQuotationLineLocal = salesQuotationLine_ds.getFirst(true)
        ? salesQuotationLine_ds.getFirst(true) : salesQuotationLine_ds.cursor();
    salesQuotationLineLocal; salesQuotationLineLocal = salesQuotationLine_ds.getNext())
    {
        if(SalesQuotationLine::findInventTransId(salesQuotationLineLocal.InventTransId))
        {
            return true;
        }
    }

    return false;

}

//BP Deviation documented
display container lineDeliveryTypeImage(SalesQuotationLine _salesQuotationLine)
{
    switch (_salesQuotationLine.LineDeliveryType)
    {
        case LineDeliveryType::OrderLineWithMultipleDeliveries:
            return OrderLineMultipleDeliveriesImageData;
        case LineDeliveryType::DeliveryLine:
            return DeliveryLineImageData;
        default:
            return connull();
    }
}

public void selectionChanged()
{
    super();
    element.enableHeaderUpdateActions();
}

FormDataSource salesQuotationTable_ds()
{
    return salesQuotationTable_ds;
}

public void linkActive()
{
    super();

    if (!salesQuotationLine && salesQuotationTable)
    {
        salesQuotationTableForm.salesQuotationTable(salesQuotationTable);
        salesQuotationLine_ds.create();
    }
}

public boolean validateDelete()
{
    boolean ret = true;
    str warning = salesQuotationLine.type().getDeleteWarning();

    if (warning != "")
    {
        ret = (Box::yesNo(warning, DialogButton::No) == DialogButton::Yes)
            && salesQuotationLine.validateDelete();
    }
    else
    {
        ret = super();
    }

    return ret;
}

public boolean validateWrite()
{
    boolean                 ret;
    ItemId                  itemId;

    salesQuotationTableForm.setTouched(salesQuotationTable);
    salesQuotationLine.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    ret = super();

    if (ret)
    {
        itemId = salesQuotationLine.ItemId;
        ret = salesQuotationTableForm.salesQuotationLine_ValidateWrite(salesQuotationLine, inventDim);
        if (itemId != salesQuotationLine.ItemId)
        {
            salesQuotationLine_ds.changedInventoriedStatus();
        }
    }

    copyTaxGroupToMarkupTrans   = DialogButton::No;
    if (ret && salesQuotationLine.TaxGroup != salesQuotationLine.orig().TaxGroup)
    {
        copyTaxGroupToMarkupTrans   = MarkupTrans::promptCopyTaxGroupToMarkupTrans(salesQuotationLine.TableId, salesQuotationLine.RecId);
        ret                         = copyTaxGroupToMarkupTrans != DialogButton::Cancel;
    }

    if (!salesQuotationLine.ItemId)
    {
        ret = checkFailed(strFmt("@DNR8619", fieldid2name(tableNum(SalesQuotationLine), fieldNum(SalesQuotationLine, ItemId))));
    }

    return ret;
}

void refreshDDC()
{
    if(salesQuotationLine_ds && salesQuotationLine && salesQuotationLine.DeliveryDateControlType)
        salesQuotationLine_ds.refresh();
}

void setTouched()
{
    // required by form MarkupTrans to communicate changes to the caller
    salesQuotationTableForm.setTouched(salesQuotationTable);
}

void  write()
{
    SalesQuotationLine localSalesQuotationLine;
    // Using this buffer to save active record to go back to it after refresh.
    RetailSalesQuotationLine localRetailSalesQuotationLine;
    MCROrderEventTable  mcrOrderEventTable;
    MCRSourceCodeSetup mcrSourceCodeSetupLocal;
    MCRSourceIdForm sourceId;
    boolean priceChanged;
    SalesQuotationLine  salesQuotationLineOrig;
    boolean showRentalLineEditor;
    boolean updateKitPricing;
    boolean lineInserted = !SalesQuotationLine.RecId;
    boolean rereadLines = false;

    salesQuotationLineOrig.data(salesQuotationLine.orig().data());

    if (salesQuotationTable.checkUpdate())
    {
        if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter))
            && (salesQuotationLine.orig().SalesPrice     != salesQuotationLine.SalesPrice
            || salesQuotationLine.orig().LineDisc    != salesQuotationLine.LineDisc
            || salesQuotationLine.orig().LinePercent != salesQuotationLine.LinePercent
            || salesQuotationLine.orig().PriceUnit   != salesQuotationLine.PriceUnit
            || salesQuotationLine.orig().SalesMarkup != salesQuotationLine.SalesMarkup))
        {
            priceChanged = true;
        }
        salesQuotationLine.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

        if (!salesQuotationLine.RecId && InventDim.InventLocationId && !salesQuotationLine.RORReturnInventLocationId)
        {
            salesQuotationLine.RORReturnInventLocationId = InventDim.InventLocationId;
        }

        salesQuotationTableForm.salesQuotationLine_Write(salesQuotationLine);

        showRentalLineEditor = RORRentalLineEditorForm::mustAutoShowRentalLineEditor(salesQuotationLine);
        updateKitPricing = (!showRentalLineEditor && salesQuotationLine.RORRentalLine::canUpdateKitPricing());

        if (copyTaxGroupToMarkupTrans == DialogButton::Yes || copyTaxGroupToMarkupTrans == DialogButton::YesToAll)
        {
            MarkupTrans::updateTaxGroup(salesQuotationLine.TableId, salesQuotationLine.RecId, salesQuotationLine.TaxGroup, salesQuotationLine.orig().TaxGroup, copyTaxGroupToMarkupTrans);
            copyTaxGroupToMarkupTrans = DialogButton::No;
        }

        ttsBegin;
        if (salesQuotationLine.RORRentalLine::canCascadeFieldsToLines(salesQuotationLineOrig))
        {
            salesQuotationLine.RORRentalLine::CascadeFieldsToLines();
            rereadLines = true;
        }

        super();

        if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
        {
            mcrSalesQuotationLine.data(salesQuotationLine.mcrSalesQuotationLine());
            mcrSalesQuotationLine_ds.refresh();
            if (!mcrSalesQuotationLine.RecId)
            {
                // need to force the write in the create case since the form won't do it.
                mcrSalesQuotationLine.SalesQuotationLine = salesQuotationLine.RecId;
                mcrSalesQuotationLine_ds.write();
            }
            mcrSalesQuotationLineOverride.data(salesQuotationLine.mcrSalesQuotationLineOverride());
            mcrSalesQuotationLineOverride_ds.refresh();
            if (!mcrSalesQuotationLineOverride.RecId && mcrSalesQuotationLineOverride.PriceOverride)
            {
                // need to force the write in the create case since the form won't do it.
                mcrSalesQuotationLineOverride.SalesQuotationLine = salesQuotationLine.RecId;
                mcrSalesQuotationLineOVerride_ds.write();
            }
            // The call to super() will trigger the header to be updated sometimes
            // so reread it just in case.
            salesQuotationTable.reread();
            salesQuotationTable_ds.refreshEx();

            // Write the order event for override price if an
            // event around pricing occured.
            if (mcrLinePriceOverrideEventType
                && priceChanged)
            {
                mcrOrderEventTable.insertFromSalesQuoteLinePriceOverride(
                    salesQuotationLine,
                    mcrLinePriceOverrideEventType,
                    mcrLinePriceOverrideUserId,
                    salesQuotationLine.mcrSalesQuotationLineOverride().ListPrice,
                    salesQuotationLine.mcrSalesQuotationLineOverride().ListPriceCurrencyCode,
                    salesQuotationLine.mcrPricePerSalesQty(),
                    retailInformationSubcodeIdLine,
                    salesQuotationLine.InventTransId);
                mcrLinePriceOverrideEventType = 0;
            }

            if (isRetailOrder)
            {
                localRetailSalesQuotationLine = RetailSalesQuotationLine::findBySalesQuotationLine(salesQuotationLine.RecId);

                if (!localRetailSalesQuotationLine)
                {
                    localRetailSalesQuotationLine.SalesQuotationLine = salesQuotationLine.RecId;
                    sourceId = salesQuotationTable.mcrSalesQuotationTable().SourceId;
                    if (sourceId)
                    {
                        mcrSourceCodeSetupLocal = MCRSourceCodeSetup::find(sourceId);
                        localRetailSalesQuotationLine.Catalog = mcrSourceCodeSetupLocal.Catalog;
                    }
                    localRetailSalesQuotationLine.insert();
                }
            }

            this.updateRetailDiscounts();
        }

        if (salesQuotationLine.InventDimId != inventDim.InventDimId)
        {
            inventDim.data(InventDim::find(salesQuotationLine.InventDimId));
            inventDim_ds.setCurrent();
        }

        if (salesQuotationLine.isCategoryBased() && !salesQuotationLineForeignTradeCategory.SalesQuotationLineInventTransId)
        {
            salesQuotationLineForeignTradeCategory.initFromSalesQuotationLine(salesQuotationLine);
            salesQuotationLineForeignTradeCategory_ds.write();
        }

        ttsCommit;

        salesQuotationTableForm.refreshDeliverySchedule(salesQuotationLine);

        salesQuotationTable_ds.reread();
        salesQuotationTable_ds.refresh();
        element.setFieldAccessHeader();

    }


    if(salesQuotationLine.SalesQty < 0)
    {
        select firstonly localSalesQuotationLine
            where localSalesQuotationLine.QuotationId == salesQuotationLine.QuotationId &&
                  localSalesQuotationLine.QtyOrdered > 0;

        if((salesQuotationTable.CashDiscPercent && !localSalesQuotationLine) || (localSalesQuotationLine && salesQuotationTable.QuotationStatus == SalesQuotationStatus::Confirmed && salesQuotationTable.CashDiscPercent))
            info("@SYS99047");

    }

    if (showRentalLineEditor)
    {
        salesQuotationLine.RORRentalLine::openRentalLineEditor(element);
    }

    if (!showRentalLineEditor || rereadLines)
    {
        localSalesQuotationLine.data(salesQuotationLine);

        if (updateKitPricing)
        {
            if (lineInserted)
            {
                RORRentalLineEditorForm::createSupplementaryItemsLines(localSalesQuotationLine);
            }
            else
            {
                ttsbegin;
                salesQuotationLine.RORRentalLine::updateKitComponentPricing(lineInserted ? RORKitPricingActionType::CreateLines : RORKitPricingActionType::ModifyLine, !lineAmountModified);
                ttscommit;
            }
        }

        salesQuotationLine_ds.research();
        salesQuotationLine_ds.positionToRecord(localSalesQuotationLine);
        lineAmountModified = false;
    }

}

/// <summary>
///     Displays the margin percent value for the sale quotation line.
/// </summary>
/// <param name="_salesQuotationLine">
///     A buffer of the sales quotation line record selected on the form.
/// </param>
/// <returns>
///     The margin percent.
/// </returns>
display MCRMarginPecentStr mcrMarginPercentStr(SalesQuotationLine _salesQuotationLine)
{
    return conPeek(this.mcrGetMarginValues(_salesQuotationLine), 1);
}

/// <summary>
///     Returns the margin percent value and margin icon for the sale quotation line.
/// </summary>
/// <param name="_salesQuotationLine">
///     A buffer of the sales quotation line record selected on the form.
/// </param>
/// <returns>
///     The margin percent, icon and textual status.
/// </returns>
private container mcrGetMarginValues(SalesQuotationLine _salesQuotationLine)
{
    if (salesParameters.mcrEnableMarginAlert
        && _salesQuotationLine.InventTransId)
    {
        // Use the value in the map for performance reasons if exists
        if (marginMap.exists(_salesQuotationLine.InventTransId))
        {
            return marginMap.lookup(_salesQuotationLine.InventTransId);
        }
        else
        {
            str marginIcon;
            MCRMarginPecentStr marginPecentStr = _salesQuotationLine.mcrDisplayMarginPercentStr();

            // If the margin could not be calculated 'N/A' will be returned so don't
            // determine the icon
            if (marginPecentStr != "@SYS180118")
            {
                marginIcon = MCRMarginAlert::getMarginSymbol(str2num(marginPecentStr));
            }
            marginMap.insert(_salesQuotationLine.InventTransId, [marginPecentStr, marginIcon]);

            return [marginPecentStr, marginIcon];
        }
    }

    return conNull();
}

/// <summary>
///     Displays the margin icon value for the sale line.
/// </summary>
/// <param name="_salesQuotationLine">
///     A buffer of the sales quotation line record selected on the form.
/// </param>
/// <returns>
///     The margin icon.
/// </returns>
display container mcrMarginIcon(SalesQuotationLine _salesQuotationLine)
{
    SymbolFontGlyphName symbol = conPeek(this.mcrGetMarginValues(_salesQuotationLine), 2);
    return ImageReference::constructForSymbol(symbol).pack();
}

public void BOBUpdateDesign()
{
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, BOBSellFixedAsset)).allowEdit(salesQuotationLine.RORTradeType == RORTradeType::SalesPurch);
}

public void refreshInventDim()
{
    if (salesQuotationLine.InventDimId != inventDim.InventDimId)
    {
        inventDim.data(InventDim::find(salesQuotationLine.InventDimId));
        inventDim_ds.setCurrent();
    }
}

public void updateLineFieldsMandatory()
{
    boolean isSalesPurch = SalesQuotationLine.RORTradeType == RORTradeType::SalesPurch;

    SalesQuotationLine_ds.object(fieldNum(SalesQuotationLine, INVInvoiceProfileId)).mandatory(!isSalesPurch);
    SalesQuotationLine_ds.object(fieldNum(SalesQuotationLine, DNRCalendarId)).mandatory(!isSalesPurch);
    SalesQuotationLine_ds.object(fieldNum(SalesQuotationLine, RORPriceTimeUnit)).mandatory(!isSalesPurch);
    SalesQuotationLine_ds.object(fieldNum(SalesQuotationLine, INVRentalModeId)).mandatory(!isSalesPurch);
}

void updateDesign(boolean _allowEditRentalPricing, boolean _allowEditSalesPricing, boolean _allowEditQty)
{
    boolean canEditCalendarProfile = SalesQuotationLine.RORTradeType != RORTradeType::SalesPurch && 
        (_allowEditRentalPricing || 
         salesQuotationLine.RORLineType != RORLineType::Normal ||
         salesQuotationLine.RORKitComponentType == RORKitComponentType::Kit ||
         salesQuotationLine.RORKitComponentType == RORKitComponentType::KitComponent);

    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, RORRentalGrossAmount)).allowEdit(_allowEditRentalPricing);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, SalesPrice)).enabled(_allowEditSalesPricing);

    // Only allow calendar changes for parent lines (will then cascade)
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, DNRCalendarId)).allowEdit(canEditCalendarProfile);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, INVInvoiceProfileId)).allowEdit(canEditCalendarProfile);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, RORPriceTimeUnit)).allowEdit(canEditCalendarProfile);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, INVRentalModeId)).allowEdit(canEditCalendarProfile);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, INVRateMultiplierTier)).allowEdit(_allowEditRentalPricing || _allowEditSalesPricing);

    salesQuotationLine_ds.object(fieldnum(SalesQuotationLine, SalesQty)).allowEdit(_allowEditQty);
    salesQuotationLine_ds.object(fieldnum(SalesQuotationLine, SalesUnit)).allowEdit(_allowEditQty);
    salesQuotationLine_ds.object(fieldnum(SalesQuotationLine, QtyOrdered)).allowEdit(_allowEditQty);
    salesQuotationLine_ds.object(fieldnum(SalesQuotationLine, SalesPrice)).allowEdit(_allowEditSalesPricing);
    salesQuotationLine_ds.object(fieldnum(SalesQuotationLine, PriceUnit)).allowEdit(_allowEditSalesPricing || _allowEditRentalPricing);
    salesQuotationLine_ds.object(fieldnum(SalesQuotationLine, LineAmount)).allowEdit(_allowEditSalesPricing);
    salesQuotationLine_ds.object(fieldnum(SalesQuotationLine, SalesMarkup)).allowEdit(_allowEditSalesPricing);
    salesQuotationLine_ds.object(fieldnum(SalesQuotationLine, LineDisc)).allowEdit(_allowEditSalesPricing);
    salesQuotationLine_ds.object(fieldnum(SalesQuotationLine, LinePercent)).allowEdit(_allowEditSalesPricing);
    salesQuotationLine_ds.object(fieldnum(SalesQuotationLine, MultiLnDisc)).allowEdit(_allowEditSalesPricing);
    salesQuotationLine_ds.object(fieldnum(SalesQuotationLine, MultiLnPercent)).allowEdit(_allowEditSalesPricing);

    salesQuotationLine_ds.object(fieldnum(SalesQuotationLine, RORReturnInventLocationId)).allowEdit(salesQuotationLine.RORTradeType != RORTradeType::SalesPurch && salesQuotationLine.RORLineType != RORLineType::Insurance);

    ROReditNetAmount.allowEdit(salesQuotationLine.RORLineType == RORLineType::Normal && !salesQuotationLine.RORIsPriceFixed);
    ROReditLineAmount.allowEdit(salesQuotationLine.RORLineType == RORLineType::Normal && !salesQuotationLine.RORIsPriceFixed);
    RORRentalGeneral_M_ROREditNetAmount.allowEdit(salesQuotationLine.RORLineType == RORLineType::Normal && !salesQuotationLine.RORIsPriceFixed);

    salesQuotationLine_ds.allowDelete(SalesQuotationLine.RORLineType == RORLineType::Normal);

    RORTogglePriceType_Fixed::updateButtonDesign(RORTogglePriceType_Fixed, salesQuotationLine);
    RORTogglePriceType_Net::updateButtonDesign(RORTogglePriceType_Net, salesQuotationLine);

    RAGContractTable_SalesQuotationLine.enabled(LineView.isActivePage() &&
                                                !TabPageGrid.isActivePage() &&
                                                salesQuotationLine.RORRentalDateTimesHost::rentalDateTimes().RecId != 0 &&
                                                RAGUtil::mustApplyRentalAgreements(salesQuotationLine.BOBRentalTypeIdRequested));

    ///BOBObjectAssignmentEngine.enabled(salesQuotationLine.inventTable().BOBIsSerializedRentalItem() &&
    ///                                  salesQuotationLine.RORTradeType != RORTradeType::SalesPurch &&
     ///                                 salesQuotationLine.QuotationStatus <= SalesQuotationStatus::Sent);

    BOBRentalTrans.enabled(salesQuotationLine.BOBRentalTypeIdRequested != '');

}

private void updateLineButtonsDesign()
{
    RORRentalLineEditorFormButton.enabled(canQuotationBeUpdated && SalesQuotationLine.RORRentalLine::canOpenRentalEditorForm());
    ROROrderAmendmentDialogType_ChangeDate_Line.enabled(salesQuotationTableForm.editLineAllowed() && salesQuotationLine.RORTradeType != RORTradeType::SalesPurch);
}

private RecId getLastSelectedQuoteLineRecId()
{
    SalesQuotationLine salesQuotationLineLocal;

    if(!lastSelectedQuoteLineRecId)
    {
        for (salesQuotationLineLocal = SalesQuotationLine_ds.getFirst(true) ? SalesQuotationLine_ds.getFirst(true) : SalesQuotationLine_ds.cursor();
             salesQuotationLineLocal;
             salesQuotationLineLocal = SalesQuotationLine_ds.getnext())
        {
            lastSelectedQuoteLineRecId = salesQuotationLineLocal.RecId;
        }
    }

    return lastSelectedQuoteLineRecId;
}

public void modified()
{
    SalesQuotationTableInteractionHelper salesQuotationTableInteractionHelper = SalesQuotationTableInteractionHelper::construct();

    super();

    salesQuotationTableInteractionHelper.initLineActions(salesQuotationTable, salesQuotationLine);
    salesQuotationTableInteractionHelper.setSalesQuotationTableForm(salesQuotationTableForm);
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common                      ret;
    CFOPAttributesDeterminer_BR cfopAttributes;

    if (BrazilParameters::isEnabled())
    {
        cfopAttributes     = CFOPAttributesDeterminer_BR::construct(salesQuotationLine);
        cfopAttributes.parmFiscalEstablishment(inventDim.inventSite().fiscalEstablishment());

        ret = CFOPLookupHelper_BR::lookupCFOPTable(_formReferenceControl, cfopAttributes);
    }

    return ret;
}

