public void lookup(FormControl _formControl, str _filterStr)
{
    // super(_formControl, _filterStr);

    FormRun formRun = classfactory.formRunClass(new Args(formStr(RetailVariantLookup)));
    FormStringControl formControl = _formControl;

    formRun.args().parm(salesQuotationLine.ItemId);
    formRun.args().caller(_formControl);
    formRun.init();

    formRun.run();
    formControl.performFormLookup(formRun);
}

public void modified()
{
    InventDimCombination    comb;

    super();

    comb = InventDimCombination::findVariantId(salesQuotationLine.RetailVariantId);
    if (comb)
    {
        inventDim.InventSizeId = comb.inventDim().InventSizeId;
        inventDim.InventColorId = comb.inventDim().InventColorId;
        inventDim.InventStyleId = comb.inventDim().InventStyleId;
        inventDim.ConfigId = comb.inventDim().ConfigId;
        // set Name according to VariantId
        salesQuotationLine.InventDimId    = InventDim::findOrCreate(inventDim).InventDimId;
        salesQuotationLine.initFromProductDimensions(inventDim);
    }
}

public void jumpRef()
{
    Args args = new Args();

    args.caller(element);
    args.record(InventTable::find(salesQuotationLine.ItemId));
    args.lookupRecord(EcoResDistinctProductVariant::find(InventDimCombination::findVariantId(salesQuotationLine.RetailVariantId).DistinctProductVariant));

    new MenuFunction(menuitemDisplayStr(EcoResProductVariantsAction), MenuItemType::Display).run(args);
}

