public void modified()
{
    DialogButton dialogButton = Box::yesNoCancel(strFmt("@DNR6681", UnitOfMeasure::find(SalesQuotationLine.RORPriceTimeUnit).Symbol), dialogButton::Yes);

    switch (dialogButton)
    {
        case DialogButton::Cancel :
            salesQuotationLine.RORPriceTimeUnit = salesQuotationLine.orig().RORPriceTimeUnit;
            //go through
        case DialogButton::No :
            // just go to super()
            break;

        default :
            salesQuotationLine.RORModifiedPriceTimeUnit();
    }

    super();
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    return EcoResCategory::resolveCategoryHierarchyRole(
        _formReferenceControl,
        EcoResCategoryNamedHierarchyRole::Sales);
}

public void modified()
{
    SalesCategory    salesCategory;
    ItemFreeTxt      name;

    // Name is defaulted from category in super(). So we save it if needed.
    if (keepName)
    {
        name                     = salesQuotationLine.Name;
    }

    salesCategory                       = salesQuotationLine.SalesCategory;
    salesQuotationTableForm.resetSalesQuotationLine(salesQuotationLine);
    salesQuotationLine.SalesCategory    = salesCategory;

    super();

    if (keepName)
    {
        salesQuotationLine.Name  = name;
    }

    // Enabled/disable fields
    salesQuotationLine_ds.changedInventoriedStatus();

    element.enableLinePCActions();

    if (salesQuotationLine.isCategoryBased())
    {
        if (salesQuotationLineForeignTradeCategory.SalesQuotationLineDataAreaId == '')
        {
            //Only create a new record if no one exist
            salesQuotationLineForeignTradeCategory_ds.allowCreate(true);
            salesQuotationLineForeignTradeCategory_ds.create();
            salesQuotationLineForeignTradeCategory_ds.allowEdit(true);
            salesQuotationLineForeignTradeCategory_ds.allowCreate(false);
        }
    }
    else
    {
        salesQuotationLineForeignTradeCategory.clearFormFields();
        salesQuotationLineForeignTradeCategory_ds.allowEdit(false);
        salesQuotationLineForeignTradeCategory_ds.refresh();
    }
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    return EcoResCategory::lookupCategoryHierarchyRole(
        _formReferenceControl,
        EcoResCategoryNamedHierarchyRole::Sales);
}

