public boolean validate()
{
    boolean ret = super();

    if (ret && enableOrderPriceControl)
    {
        ret = salesQuotationLine_ds.mcrGetOverrideResponse(fieldNum(RetailSalesQuotationLine, LineManualDiscountPercentage),
                                                        retailSalesQuotationLine.LineManualDiscountPercentage,
                                                        fieldNum(MCRPriceOverrideMarkUpGroup, DiscPercentOverride));
    }

    return ret;
}

