void clicked()
{
    if(SalesQuotationPriceSimTable::find(salesQuotationLine.QuotationId))
    {
        if(Box::yesNo("@SYS100176",DialogButton::No) == DialogButton::Yes)
        {
            salesQuotationTable.updateMultiLineDisc();

            element.calculateTotalDiscount();
        }

    }
    else
    {
        salesQuotationTable.updateMultiLineDisc();

        element.calculateTotalDiscount();
    }

    salesQuotationLine_ds.reread();
    salesQuotationLine_ds.refresh();

    super();
}

