public boolean modified()
{
    boolean ret;

    ret = super();

    if (!salesQuotationTable.ContactPersonId)
    {
        if (!ContactPerson::findNameParty(salesQuotationTable.party(), RORContactPersonHeader_M_editContactPersonName.text()))
        {
            salesQuotationTable.ContactPersonId = ContactPerson::findOrCreateNameParty(salesQuotationTable.party(), RORContactPersonHeader_M_editContactPersonName.text()).ContactPersonId;
        }
    }

    return ret;
}

