void clicked()
{
    Args            args = new Args();
    FormRun         deliverySchedule;

    args.name(formStr(SalesQuotationDeliverySchedule));
    args.caller(this);
    args.record(salesQuotationLine);
    deliverySchedule = classfactory.formRunClass(args);

    deliverySchedule.run();
    deliverySchedule.wait();

    if (deliverySchedule.closedOk())
    {
        salesQuotationLine_ds.reread();
        salesQuotationLine_ds.rereadReferenceDataSources();
        salesQuotationLine_ds.research(true);
        salesQuotationTableForm.setTouched(salesQuotationTable);
    }
}

