public void jumpRef()
{
    Args args;
    ;

    args = new Args();
    args.caller(element);

    if(this.text())
    {
        args.lookupField(fieldNum(BOBObjectTable, ObjectId));
        args.lookupValue(this.text());
    }
    else
    {
        args.formViewOption(FormViewOption::Grid);
    }

    new MenuFunction(menuitemDisplayStr(BOBObjectTable), MenuItemType::Display).run(args);
}

