public boolean pmfIsEnabled()
{
    return salesLine.inventTable().isFormulaAllowed();
}

void recalculateRetailPricesDiscounts()
{
    RetailSalesTable retailSales = salesTable.retailSalesTable();

    RetailSalesOrderCalculator::setPricesDiscountsOnOrderOnServer(salesTable.SalesId, retailSales.TotalManualDiscountPercentage);

    retailOrderNeedRecalculation = false;

    salesLine_ds.reread();
    salesLine_ds.refresh();
}

