/// <summary>
/// Updated ECommerce field and control.
/// </summary>
public void updateECommerce_IN()
{
    if (isIN)
    {
        TaxInformationCustTable_IN taxInformationCustTableIN = TaxInformationCustTable_IN::findByCustTable(salesTable.CustAccount);
        if (taxInformationCustTableIN.DefaultECommerceOperator)
        {
            salesTable_W.eCommerceSale_IN         = NoYes::Yes;
            ECommerce_IN_ECommerceOperator_IN.enabled(true);
            ECommerce_IN_ECommerceOperatorGSTIN_IN.enabled(true);
            ECommerce_IN_MerchantID_IN.enabled(true);
            salesTable_W.eCommerceOperator_IN     = taxInformationCustTableIN.DefaultECommerceOperator;
            salesTable_W.MerchantID_IN            = TaxInformationCustTable_IN::findByCustTable(salesTable_W.eCommerceOperator_IN).MerchantID;
            salesTable_W.eCommerceOperatorGSTIN_IN = TaxInformation_IN::findDefaultbyLocation(DirPartyLocation::findPrimaryPartyLocation(CustTable::find(taxInformationCustTableIN.DefaultECommerceOperator).Party).Location).gstin;
        }
        else
        {
            salesTable_W.eCommerceSale_IN     = NoYes::No;
            ECommerce_IN_ECommerceOperator_IN.enabled(false);
            ECommerce_IN_ECommerceOperatorGSTIN_IN.enabled(false);
            ECommerce_IN_MerchantID_IN.enabled(false);
            salesTable_W.eCommerceOperator_IN = '';
            salesTable_W.MerchantID_IN        = '';
            salesTable_W.eCommerceOperatorGSTIN_IN = 0;
        }
        salesTable_W_ds.refresh();
    }
}

