}

void RORmodifyQueryBusRel()
{
    smmBusRelTable          smmBusRelTable;
    CustAccount             custAccount;
    QueryBuildDataSource    qbdsSalesTable;

    smmBusRelTable = element.args().record();
    custAccount    = smmBusRelTable.custAccount();

    if (custAccount)
    {
        qbdsSalesTable = salesTable_ds.query().dataSourceTable(tableNum(SalesTable));
        qbdsSalesTable.clearDynalinks();
        qbdsSalesTable.addRange(fieldNum(SalesTable, CustAccount)).value(custAccount);
    }
}

