{
    if (!salesTableType)
    {
        salesTableType = salesTable.type();
    }

    boolean canCreateSubRentalAndBacktoBack = SalesTable.FINCreditCheckNoWarning(true, false) &&
                                                 salesTableType.RORhasLineToCreatePurchOrder() &&
                                                 salesTableType.canCreatePurchOrder();

    RORCreateSubRentalOrderMultipleLines.enabled(canCreateSubRentalAndBacktoBack);

    DNRBackToBackHeader.enabled(canCreateSubRentalAndBacktoBack);

}

