public void modified()
{
    MCRCustPaymTable mcrCustPaymTableCount;

    if (mcrCallCenterEnabled)
    {
        // Invoice account has changed so back out any existing payments
        select count(RecId) from mcrCustPaymTableCount
            where mcrCustPaymTableCount.RefRecId   == salesTable.RecId
               && mcrCustPaymTableCount.RefTableId == salesTable.TableId;

        // if payment records currently exist
        if(mcrCustPaymTableCount.RecId > 0)
        {
            // Inform user they are about to lose their payment records and ask
            // if they wish to continue.
            if (Box::yesNo("@MCR12786", DialogButton::Yes) == DialogButton::No)
            {
                salesTable.CustAccount = salesTable.orig().CustAccount;
                salestable_ds.refresh();
                return;
            }
        }
    }

    super();

    // Update default loyalty card based on the customer.
    if (isRetailOrder)
    {
        RetailLoyaltyCard card = RetailLoyaltyCard::findDefaultLoyaltyCardByCustomer(salesTable.CustAccount);
        retailSalesTable.RetailLoyaltyCard = card.RecId;
        salesTable.packRetailSalesTable(retailSalesTable);
    }

    if ((select firstonly salesLine where salesLine.SalesId == salesTable.SalesId).RecId)
    {
        salesLine_ds.executeQuery();
    }

    // <GEERU>
    if (isRU)
    {
        salesTable_ds.cacheCalculateMethod(tableMethodStr(SalesTable, agreementDocumentTitle_RU));
    }
    // </GEERU>

    salesTable_ds.cacheCalculateMethod(tableMethodStr(SalesTable, editContactPersonName));
    // <GIN>
    salesTable_ds.updateECommerce_IN();
    // </GIN>
    salesTable_ds.refresh();

    salesTable_ds.rereadReferenceDataSources();

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        salesTable_BR_ds.refresh();
    }
    // </GBR>

    if (TaxIntegrationUtils::isTaxInformationEnabled())
    {
        CustTable custTable = CustTable::find(salesTable.CustAccount);
        SalesLine salesLineLoc;

        while select salesLineLoc
            where salesLineLoc.SalesId == salesTable.SalesId
        {
            TransTaxInformation transTaxInformationLocal = TransTaxInformationHelper::findOrCreateTransTaxInformation(salesLine.TableId, salesLine.RecId);
            if (transTaxInformationLocal)
            {
                ttsbegin;
                transTaxInformationLocal.selectForUpdate(true);
                TransTaxInformationHelper::initFromCustVend(transTaxInformationLocal, custTable);
                transTaxInformationLocal.update();
                ttscommit;
            }
        }
    }
}

