/// <summary>
/// Deletes the related direct delivery purchase order.
/// </summary>
/// <param name = "_mcrDropShipmentOrigValue">The original <c>MCRDropShipment</c> value retrieved from the orig buffer.</c></param>
/// <remarks>
/// This is used for cases when user un-checks the drop shipment flag.
/// </remarks>
protected void deleteDirectDeliveryPurchaseOrder(MCRDropShipment _mcrDropShipmentOrigValue = NoYes::No)
{
    if(salesLine
        && mcrSalesLineDropShipment
        && !mcrSalesLineDropShipment.DropShipment
        && _mcrDropShipmentOrigValue)
    {
        MCRSalesLineDropShipment::deleteRelatedPurchaseOrderLine(salesLine.RecId);
        salesLine_ds.reread();
    }
}

/// <summary>
///     Returns the margin percent value and margin icon for the sale line.
/// </summary>
/// <param name="_salesLine">
///     A buffer of the sales line record selected on the form.
/// </param>
/// <returns>
///     The margin percent, icon and textual status.
/// </returns>
private container mcrGetMarginValues(SalesLine _salesLine)
{
    if (salesParameters.mcrEnableMarginAlert
        && _salesLine.InventTransId)
    {
        // Use the value in the map for performance reasons if exists
        if (marginMap.exists(_salesLine.InventTransId))
        {
            return marginMap.lookup(_salesLine.InventTransId);
        }
        else
        {
            str marginIcon;
            MCRMarginPecentStr marginPecentStr = _salesLine.mcrDisplayMarginPercentStr();

            // If the margin could not be calculated 'N/A' will be returned so don't
            // determine the icon
            if (marginPecentStr != "@SYS180118")
            {
                marginIcon = MCRMarginAlert::getMarginSymbol(str2num(marginPecentStr));
            }
            marginMap.insert(_salesLine.InventTransId, [marginPecentStr, marginIcon]);

            return [marginPecentStr, marginIcon];
        }
    }

    return conNull();
}

/// <summary>
///     Displays the margin percent value for the sale line.
/// </summary>
/// <param name="_salesLine">
///     A buffer of the sales line record selected on the form.
/// </param>
/// <returns>
///     The margin percent.
/// </returns>
display MCRMarginPecentStr mcrMarginPercentStr(SalesLine _salesLine)
{
    container marginAlertValues = this.mcrGetMarginValues(_salesLine);
    return conPeek(marginAlertValues, 1);
}

/// <summary>
///     Displays the margin icon value for the sale line.
/// </summary>
/// <param name="_salesLine">
///     A buffer of the sales line record selected on the form.
/// </param>
/// <returns>
///     The margin icon.
/// </returns>
display container mcrMarginIcon(SalesLine _salesLine)
{
    SymbolFontGlyphName symbol = conPeek(this.mcrGetMarginValues(_salesLine), 2);
    return ImageReference::constructForSymbol(symbol).pack();
}

/// <summary>
/// If retail order, discount is recalculated after deleting line.
/// </summary>
/// <param name="_salesLine">
/// Sales line record on the grid
/// </param>
private void retailDiscountRecalculate(SalesLine _salesLine)
{
    RetailSalesTable           retailSales = salesTable.retailSalesTable();
    ;
    if (RetailParameters::isRetailEnabledAndInUse() &&
        RetailPricingEngine::useRetailPricing(_salesLine))
    {
        RetailSalesOrderCalculator::setPricesDiscountsOnOrderOnServer(salesTable.SalesId, retailSales.TotalManualDiscountPercentage);
        salesLine_ds.research(true);
    }
}

public void deleteMarked()
{
    inDeleteMarked = true;
    super();
    inDeleteMarked = false;

    this.retailDiscountReCalculate(salesLine);
}

public void BOBUpdateDesign()
{
    salesLine_ds.object(fieldNum(SalesLine, BOBSellFixedAsset)).allowEdit(salesLine.RORTradeType == RORTradeType::SalesPurch && SalesLine.SalesQty > 0);
    BOBFixedAssets_M_BOBEditAssetId.allowEdit(salesLine.RORTradeType == RORTradeType::SalesPurch);
    salesLine_ds.object(fieldNum(SalesLine, Reservation)).allowEdit(salesLine.RORTradeType == RORTradeType::SalesPurch);
}

display ImageRes lineOnHandCheckImage(SalesLine _salesLine)
{
    #ResAppl
    ErrorTolerance result;

    if (lineResultsCheckOnHand && !lineResultsCheckOnHand.empty() && lineResultsChecKOnHand.exists(_salesLine.RecId))
    {
        result = lineResultsCheckOnHand.lookup(_salesLine.RecId);

        switch (result)
        {
            case ErrorTolerance::Accept:
                return #ImageOK;

            case ErrorTolerance::Warning:
                return #ImageWarning;

            case ErrorTolerance::Error:
                return #ImageError;
        }
    }

    return 0;
}

public void refreshInventDim()
{
    if (salesLine.InventDimId != inventDim.InventDimId)
    {
        inventDim.data(InventDim::find(salesLine.InventDimId));
        inventDim_ds.setCurrent();
    }
}

private RecId getLastSelectedSalesLineRecId()
{
    SalesLine salesLineLocal;
    ;

    if(!lastSelectedSalesLineRecId)
    {
        for(salesLineLocal = SalesLine_ds.getFirst(true) ? SalesLine_ds.getFirst(true) : SalesLine_ds.cursor();
            salesLineLocal;
            salesLineLocal = SalesLine_ds.getnext())
        {
            lastSelectedSalesLineRecId = salesLineLocal.RecId;
        }
    }

    return lastSelectedSalesLineRecId;
}

void updateDesign(boolean   _allowEditRentalPricing,
                  boolean   _allowEditSalesPricing,
                  boolean   _allowEditQty)
{
    ItemId itemIdWarranty = RAGParameters::find().ItemIdWarranty;
    boolean canEditCalendarProfile = SalesLine.RORTradeType != RORTradeType::SalesPurch &&
        (_allowEditRentalPricing ||
         salesLine.ItemId == itemIdWarranty ||
         salesLine.RORKitComponentType == RORKitComponentType::Kit ||
         salesLine.RORKitComponentType == RORKitComponentType::KitComponent);

    salesLine_ds.object(fieldNum(SalesLine, RORRentalGrossAmount)).allowEdit(_allowEditRentalPricing);
    salesLine_ds.object(fieldNum(SalesLine, SalesPrice)).enabled(_allowEditSalesPricing);

    // Only allow calendar changes for parent lines (will then cascade)
    salesLine_ds.object(fieldNum(SalesLine, DNRCalendarId)).allowEdit(canEditCalendarProfile);
    salesLine_ds.object(fieldNum(SalesLine, INVInvoiceProfileId)).allowEdit(canEditCalendarProfile);
    salesLine_ds.object(fieldNum(SalesLine, RORPriceTimeUnit)).allowEdit(canEditCalendarProfile);
    salesLine_ds.object(fieldNum(SalesLine, INVRentalModeId)).allowEdit(canEditCalendarProfile);
    salesLine_ds.object(fieldNum(SalesLine, INVRateMultiplierTier)).allowEdit(_allowEditRentalPricing || _allowEditSalesPricing);

    salesLine_ds.object(fieldnum(SalesLine, SalesQty)).allowEdit(_allowEditQty);
    salesLine_ds.object(fieldnum(SalesLine, SalesUnit)).allowEdit(_allowEditQty);
    salesLine_ds.object(fieldnum(SalesLine, QtyOrdered)).allowEdit(_allowEditQty);
    salesLine_ds.object(fieldnum(SalesLine, SalesPrice)).allowEdit(_allowEditSalesPricing);
    salesLine_ds.object(fieldnum(SalesLine, PriceUnit)).allowEdit(_allowEditSalesPricing);
    salesLine_ds.object(fieldnum(SalesLine, LineAmount)).allowEdit(_allowEditSalesPricing);
    salesLine_ds.object(fieldnum(SalesLine, SalesMarkup)).allowEdit(_allowEditSalesPricing);
    salesLine_ds.object(fieldnum(SalesLine, LineDisc)).allowEdit(_allowEditSalesPricing);
    salesLine_ds.object(fieldnum(SalesLine, LinePercent)).allowEdit(_allowEditSalesPricing);
    salesLine_ds.object(fieldnum(SalesLine, MultiLnDisc)).allowEdit(_allowEditSalesPricing);
    salesLine_ds.object(fieldnum(SalesLine, MultiLnPercent)).allowEdit(_allowEditSalesPricing);

    salesLine_ds.object(fieldnum(SalesLine, RORReturnInventLocationId)).allowEdit(salesLine.RORTradeType != RORTradeType::SalesPurch && salesLine.BOBRentalSubStatus < BOBRentalSubStatus::Returned);

    ROReditNetAmount.allowEdit(SalesLine.ItemId != itemIdWarranty && !SalesLine.RORIsPriceFixed);
    ROReditLineAmount.allowEdit(SalesLine.ItemId != itemIdWarranty && !SalesLine.RORIsPriceFixed);
    RORPricing_DNRSalesLineTable_Extension_ROREditNetAmount.allowEdit(SalesLine.ItemId != itemIdWarranty && !SalesLine.RORIsPriceFixed);

    RORTogglePriceType_Fixed::updateButtonDesign(RORTogglePriceType_Fixed, salesLine);
    RORTogglePriceType_Net::updateButtonDesign(RORTogglePriceType_Net, salesLine);

    RAGContractTable_SalesLine.enabled(LineView.isActivePage()      &&
                                      !TabPageGrid.isActivePage()   &&
                                       salesLine.RORRentalDateTimesHost::rentalDateTimes().RecId != 0 &&
                                       RAGUtil::mustApplyRentalAgreements(salesLine.BOBRentalTypeIdRequested));

    BOBRentalTrans.enabled(salesLine.BOBRentalTypeIdRequested != '');

}

private void updateLineFieldsMandatory()
{
    boolean isSalesPurch = SalesLine.RORTradeType == RORTradeType::SalesPurch;

    SalesLine_ds.object(fieldNum(SalesLine, INVInvoiceProfileId)).mandatory(!isSalesPurch);
    SalesLine_ds.object(fieldNum(SalesLine, DNRCalendarId)).mandatory(!isSalesPurch);
    SalesLine_ds.object(fieldNum(SalesLine, RORPriceTimeUnit)).mandatory(!isSalesPurch);
    SalesLine_ds.object(fieldNum(SalesLine, INVRentalModeId)).mandatory(!isSalesPurch);
}

private void updateLineButtonsDesign()
{
    boolean canCreateSubRentalAndBacktoBack = salesLine.RORTradeType == RORTradeType::Rental &&
                                    salesLine.SalesType == SalesType::RORRental              &&
                                    salesLine.SalesStatus == SalesStatus::Backorder          &&
                                    salesLine.RemainInventPhysical != 0                      &&
                                    SalesTable.FINCreditCheckNoWarning(true, false)          &&
                                    salesTable.type().canCreatePurchOrder()                  &&
                                    // line should not have object assigned yet
                                    !salesLine.inventDim().inventSerialId                   &&
                                    salesLine.type().RORcanCreatePurchOrder();

    RORCreateSubRentalOrder.enabled(canCreateSubRentalAndBacktoBack);
    DNRPurchCreateFromSalesOrder_Back2BackLines.enabled(canCreateSubRentalAndBacktoBack);

    RORSubrentalReference.enabled((salesLine.RORTradeType == RORTradeType::SubRental || SalesTable.InterCompanyDirectDeliveryOrig) && RORSubrentalReference::existSales(SalesLine.InventTransId));            

    RORRentalLineEditorFormButton.enabled(SalesLine.RORRentalLine::canOpenRentalEditorForm());
}

