    FormStringControl formControl = _formControl;

    formRun.args().parm(salesLine.ItemId);
    formRun.args().caller(_formControl);
    formRun.init();

    formRun.run();
    formControl.performFormLookup(formRun);
}

public void modified()
{
    convertPrices = false;
    DialogButton dialogButton = Box::yesNoCancel(strFmt("@DNR6681", UnitOfMeasure::find(SalesLine.RORPriceTimeUnit).Symbol), dialogButton::Yes);

    switch (dialogButton)
    {
        case DialogButton::Cancel :
            SalesLine.RORPriceTimeUnit = SalesLine.orig().RORPriceTimeUnit;
            //go through
        case DialogButton::No :
            // just go to super()
            break;

        default :
            SalesLine.RORModifiedPriceTimeUnit();
            convertPrices = true;
    }

    super();
}

