{
    if (tmsSalesTable.RecId == 0)
    {
        tmsSalesTable.initFromSalesTable(salesTable.SalesId);
    }

    super();

    salesTable_ds.reread();
    salesTable_ds.refresh();

    element.enableTMSFields();
}

