        SalesTable_W.MerchantID_IN              = TaxInformationCustTable_IN::findByCustTable(SalesTable_W.eCommerceOperator_IN).MerchantID;
    }
    else
    {
        SalesTable_W.eCommerceOperatorGSTIN_IN  = 0;
        SalesTable_W.MerchantID_IN              = '';
    }
