public boolean validate()
{
    boolean ret = true;
    LogisticsPostalAddress   dlvAddress;

    dlvAddress = LogisticsPostalAddress::findRecId(SalesTable.DeliveryPostalAddress);

    if (dlvAddress &&
        RetailDlvModeAddressLine::isAddressExcluded(dlvAddress.CountryRegionId, dlvAddress.State, this.text()))
    {
        ret = checkFailed(strFmt("@Retail:MCRDlvModeAddressError", SalesLine.ItemId));
    }

    return ret;
}

