public boolean canClose()
{
    boolean ret;

    ret = super();

    if (element.closedOk())
    {
        if (selectedResourceIds.empty())
        {
            ret = checkFailed("@DNR7567");
        }

        if (dlgChangeAvailability.checked()
        &&  isAvailable
        && !dlgReasonCode.text())
        {
            ret = checkFailed(strFmt("@SYS26332", "@SYS63086"));
        }
    }

    return ret;
}

public void closeOk()
{
    super();

    if (element.closedOk())
    {
        WPPTaskAssignmentEngine_Service::construct().assignToMultipleResources(callerWorkOrderLine,
                                                                               PlanBy.selection(),
                                                                               selectedResourceIds,
                                                                               PlanningStartDateTime.dateTimeValue(),
                                                                               PlanningEndDateTime.dateTimeValue(),
                                                                               resourceIdAssigned);

        if (dlgChangeAvailability.checked())
        {
            BOBObjectView objectView;
            WPPWorkOrderTable workOrderTable;

            select firstonly objectView
                exists join workOrderTable
                where workOrderTable.WorkOrderId == callerWorkOrderLine.WorkOrderId
                &&    workOrderTable.BOBObjectTableRecId == objectView.RecId;

            BOBObjectManager::changeObjectAvailability(objectView.ObjectId, !objectView.IsAvailable, dlgReasonCode.text(), callerWorkOrderLine.WorkOrderId);
        }
    }
}

