[SuppressBPWarningAttribute("BPCheckInvalidInitFormMethodWarning", "Validation of caller must be performed before invoking super()")]
public void init()
{
    Args            args;
    TransDate       requestedStartDate;
    TransDate       requestedEndDate;
    TransDateTime   requestedStartDateTime;
    TransDateTime   requestedEndDateTime;
    boolean         applyTimeZoneOffset;

    args = element.args();
    applyTimeZoneOffset = true;

    switch(element.args().dataset())
    {
        case tableNum(WPPWorkOrderLine):
            callerWorkOrderLine = element.args().record();
            break;

        case tableNum(WPPWorkOrderLineResource):
            callerWorkOrderLineResource = element.args().record();
            callerWorkOrderLine = callerWorkOrderLineResource.workOrderLine();
            applyTimeZoneOffset = false;
            break;

        default:
            throw error(strFmt("@SYS19096", formStr(WPPAssignTaskDialog), tableStr(WPPWorkOrderLine)));
    }

    super();

    if (args.parmEnumType() == enumNum(WrkCtrGroupWrkCtr))
    {
        planBy.selection(args.parmEnum());
    }

    if (args.caller()
    &&  args.caller().name() == formStr(WPPServicePlanboard))
    {
        PlanBy.enabled(false);
        PlanBy.colorScheme(FormColorScheme::WindowsPalette);
        PlanBy.foregroundColor(WindowsPalette::InactiveTitleBar);
    }

    if (PlanBy.selection() == WrkCtrGroupWrkCtr::WrkCtr
    &&  WPPParameters::find().CapabilityValidation != WPPCapabilityValidation::None)
    {
        CapabilitiesFilter.value(1);
    }

    requestedStartDate = callerWorkOrderLineResource ? DateTimeUtil::date(callerWorkOrderLineResource.PlannedFromDateTime) : DateTimeUtil::date(callerWorkOrderLine.RequestedStartDateTime);

    if (requestedStartDate < DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()))
    {
        if (callerWorkOrderLineResource)
        {
            requestedEndDate = DateTimeUtil::date(callerWorkOrderLineResource.PlannedToDateTime);

            requestedStartDateTime = DateTimeUtil::newDateTime(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()), DateTimeUtil::time(callerWorkOrderLineResource.PlannedFromDateTime));
            requestedEndDateTime   = DateTimeUtil::newDateTime(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()) + (requestedEndDate - requestedStartDate), DateTimeUtil::time(callerWorkOrderLineResource.PlannedToDateTime));
        }
        else
        {
            requestedEndDate = DateTimeUtil::date(callerWorkOrderLine.RequestedEndDateTime);

            requestedStartDateTime = DateTimeUtil::newDateTime(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()), DateTimeUtil::time(callerWorkOrderLine.RequestedStartDateTime));
            requestedEndDateTime   = DateTimeUtil::newDateTime(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()) + (requestedEndDate - requestedStartDate), DateTimeUtil::time(callerWorkOrderLine.RequestedEndDateTime));
        }

        if (applyTimeZoneOffset)
        {
            PlanningStartDateTime.dateTimeValue(DateTimeUtil::applyTimeZoneOffset(requestedStartDateTime, DateTimeUtil::getUserPreferredTimeZone()));
            PlanningEndDateTime.dateTimeValue(DateTimeUtil::applyTimeZoneOffset(requestedEndDateTime, DateTimeUtil::getUserPreferredTimeZone()));
        }
        else
        {
            PlanningStartDateTime.dateTimeValue(DateTimeUtil::removeTimeZoneOffset(requestedStartDateTime, DateTimeUtil::getUserPreferredTimeZone()));
            PlanningEndDateTime.dateTimeValue(DateTimeUtil::removeTimeZoneOffset(requestedEndDateTime, DateTimeUtil::getUserPreferredTimeZone()));
        }
    }
    else
    {
        if (callerWorkOrderLineResource)
        {
            PlanningStartDateTime.dateTimeValue(DateTimeUtil::applyTimeZoneOffset(callerWorkOrderLineResource.PlannedFromDateTime, DateTimeUtil::getUserPreferredTimeZone()));
            PlanningEndDateTime.dateTimeValue(DateTimeUtil::applyTimeZoneOffset(callerWorkOrderLineResource.PlannedToDateTime, DateTimeUtil::getUserPreferredTimeZone()));
        }
        else
        {
            PlanningStartDateTime.dateTimeValue(DateTimeUtil::applyTimeZoneOffset(callerWorkOrderLine.RequestedStartDateTime, DateTimeUtil::getUserPreferredTimeZone()));
            PlanningEndDateTime.dateTimeValue(DateTimeUtil::applyTimeZoneOffset(callerWorkOrderLine.RequestedEndDateTime, DateTimeUtil::getUserPreferredTimeZone()));
        }
    }

    element.initSelectionSet();
    tmpData = new WPPTaskAssignmentDialogData(callerWorkOrderLine.WorkOrderId, element.args().parmEnum());
    TmpAssignTask_ds.repopulate();
    element.updateDesign();
}

private void initSelectionSet()
{
    WPPWorkOrderLineResource resourcePlanning;

    selectedResourceIds = new Set(Types::String); 

    while select ResourceId from resourcePlanning
    where resourcePlanning.WorkOrderLineRecId == callerWorkOrderLine.RecId
    {
        selectedResourceIds.add(resourcePlanning.ResourceId);
    }
}

