public void updateDesign()
{
    WPPWorkOrderTaskTable   taskTable;
    WrkCtrGroupWrkCtr       planByGroupResource;

    taskTable = WPPWorkOrderTaskTable::find(callerWorkOrderLine.TaskId);
    isAvailable = WPPWorkOrderTable::BOBisObjectAvailable(callerWorkOrderLine.WorkOrderId);
    planByGroupResource = planBy.selection();

    if ((WPPWorkOrderTaskTable::lineStatusNone2LineStatus(taskTable.SetObjectAvailStatus) == WPPWorkOrderLineStatus::Planned && !isAvailable)
    ||  (WPPWorkOrderTaskTable::lineStatusNone2LineStatus(taskTable.SetObjectNotAvailStatus) == WPPWorkOrderLineStatus::Planned && isAvailable))
    {
        dlgChangeAvailability.allowEdit(true);
    }
    else
    {
        dlgChangeAvailability.allowEdit(false);
    }

    dlgWorkOrderId.text(callerWorkOrderLine.WorkOrderId);
    CapabilitiesFilter.allowEdit(planByGroupResource == WrkCtrGroupWrkCtr::WrkCtr);

    TmpAssignTask_ds.object(fieldNum(WPPTmpAssignTask, AssignObject)).visible(planByGroupResource == WrkCtrGroupWrkCtr::WrkCtrGroup);
}

public void executeQuery()
{
    Query                   query;
    QueryBuildDataSource    qbdsTmpAssignTask;
    Num                     tmpResourceAccountFilter;
    WrkCtrName              tmpNameFilter;
    int                     startPosFilterField;
    int                     startPosFilterFieldValue;
    int                     endPosFilterFieldValue;
    str                     queryString;

    #DNRConstants
    #Task

    if (taskExecuted == #removeFilterSort)
    {
        qbdsTmpAssignTask = this.query().dataSourceTable(tableNum(WPPTmpAssignTask));
        qbdsTmpAssignTask.clearRanges();
    }

    // Check the task that was executed
    if (spbSettings.PlanBy == WrkCtrGroupWrkCtr::WrkCtrGroup
    && (taskExecuted == #taskShortCutMenuFilter
    ||  taskExecuted == #taskShortCutMenuFindValue
    ||  taskExecuted == #taskFilterByGrid
    ||  taskExecuted == #taskFilter
    ||  taskExecuted == #taskShortCutMenuFind))
    {
        // Initial query (related) objects
        qbdsTmpAssignTask = this.queryRunQueryBuildDataSource();
        queryString = qbdsTmpAssignTask.toString();
        query = this.query();

        // Get the position of the ResourceAccount field in the query
        startPosFilterField = strScan(queryString, fieldStr(WPPTmpAssignTask, ResourceAccount), 0, strLen(queryString));

        if (startPosFilterField)
        {
            // 1. Get the starting position of the filter value
            // 2. Get the end position of the filter value
            // 3. Get the filter value
            startPosFilterFieldValue = strScan(queryString, "N'", startPosFilterField, strLen(queryString)) + 2;
            endPosFilterFieldValue   = strScan(queryString, "'", startPosFilterFieldValue, strLen(queryString));
            tmpResourceAccountFilter = subStr(queryString, startPosFilterFieldValue, endPosFilterFieldValue - startPosFilterFieldValue);
        }

        // Get the position of the ResourceAccount field in the query
        startPosFilterField = strScan(queryString, fieldStr(WPPTmpAssignTask, Name), 0, strLen(queryString));

        if (startPosFilterField)
        {
            // 1. Get the starting position of the filter value
            // 2. Get the end position of the filter value
            // 3. Get the filter value
            startPosFilterFieldValue = strScan(queryString, "N'", startPosFilterField, strLen(queryString)) + 2;
            endPosFilterFieldValue   = strScan(queryString, "'", startPosFilterFieldValue, strLen(queryString));
            tmpNameFilter            = subStr(queryString, startPosFilterFieldValue, endPosFilterFieldValue - startPosFilterFieldValue);
        }

        // Is the current applied filter different than the previous filter
        // then apply the new filter
        if (tmpResourceAccountFilter
        &&  tmpResourceAccountFilter != resourceAccountFilter)
        {
            qbdsTmpAssignTask = query.dataSourceTable(tableNum(WPPTmpAssignTask));

            while (qbdsTmpAssignTask.findRange(fieldNum(WPPTmpAssignTask, DataAreaId)))
            {
                qbdsTmpAssignTask.clearRange(fieldNum(WPPTmpAssignTask, DataAreaId));
            }

            while select ResourceAccount, ParentResourceAccount from TmpAssignTask
            where TmpAssignTask.ResourceAccount like tmpResourceAccountFilter
            {
                qbdsTmpAssignTask.addRange(fieldNum(WPPTmpAssignTask, DataAreaId)).value(strFmt('((%1 == "%3") || (%2 == "%3"))',
                                                                                                fieldStr(WPPTmpAssignTask, ResourceAccount),
                                                                                                fieldStr(WPPTmpAssignTask, ParentResourceAccount),
                                                                                                queryValue(TmpAssignTask.ParentResourceAccount ? TmpAssignTask.ParentResourceAccount : TmpAssignTask.ResourceAccount)));
            }

            this.queryRun(new QueryRun(query));
        }
        else
        if (tmpNameFilter
        &&  tmpNameFilter != nameFilter)
        {
            qbdsTmpAssignTask = query.dataSourceTable(tableNum(WPPTmpAssignTask));

            while (qbdsTmpAssignTask.findRange(fieldNum(WPPTmpAssignTask, DataAreaId)))
            {
                qbdsTmpAssignTask.clearRange(fieldNum(WPPTmpAssignTask, DataAreaId));
            }

            while select ResourceAccount, ParentResourceAccount from TmpAssignTask
            where TmpAssignTask.Name like tmpNameFilter
            {
                qbdsTmpAssignTask.addRange(fieldNum(WPPTmpAssignTask, DataAreaId)).value(strFmt('((%1 == "%3") || (%2 == "%3"))',
                                                                                                fieldStr(WPPTmpAssignTask, ResourceAccount),
                                                                                                fieldStr(WPPTmpAssignTask, ParentResourceAccount),
                                                                                                queryValue(TmpAssignTask.ParentResourceAccount ? TmpAssignTask.ParentResourceAccount : TmpAssignTask.ResourceAccount)));
            }

            this.queryRun(new QueryRun(query));
        }
    }

    super();

    // Store the current filter globally
    resourceAccountFilter = tmpResourceAccountFilter;
    nameFilter = tmpNameFilter;
}

