private boolean mustParentUnchecked(WPPResourceId _parentResourceId)
{
    SetEnumerator       enumerator;
    WPPResourceId       resourceId;
    WPPTmpAssignTask    tmpAssignTaskFind;

    enumerator = selectedResourceIds.getEnumerator();
    tmpAssignTaskFind.data(TmpAssignTask);

    while (enumerator.moveNext())
    {
        resourceId = enumerator.current();

        select firstOnly RecId from tmpAssignTaskFind
        where tmpAssignTaskFind.ResourceAccount == resourceId
        &&    tmpAssignTaskFind.ParentResourceAccount == _parentResourceId;

        if (tmpAssignTaskFind)
        {
            return true;
        }
    }

    return false;
}

public void init()
{
    QueryBuildDataSource    qbdsTmpAssignTask;

    super();

    qbdsTmpAssignTask = this.query().dataSourceTable(tableNum(WPPTmpAssignTask));

    if (element.args().parmEnum() == WrkCtrGroupWrkCtr::WrkCtrGroup)
    {
        qbdsTmpAssignTask.addSortField(fieldNum(WPPTmpAssignTask, GroupSortingNumber), SortOrder::Ascending);
        qbdsTmpAssignTask.addSortField(fieldNum(WPPTmpAssignTask, IsIndividualResource), SortOrder::Ascending);
    }

    qbdsTmpAssignTask.addSortField(fieldNum(WPPTmpAssignTask, CalculatedLevel), SortOrder::Descending);
    qbdsTmpAssignTask.addSortField(fieldNum(WPPTmpAssignTask, PrioritySum), SortOrder::Ascending);
    qbdsTmpAssignTask.addSortField(fieldNum(WPPTmpAssignTask, SortingNumber), SortOrder::Ascending);

}

