edit NoYes editSelected(boolean             _set,
                        WPPTmpAssignTask    _resource,
                        NoYes               _newValue)
{
    NoYes               ret;
    WPPResourceTable    resourceTable;
    WPPResourceId       parentResourceId;

    if (_set)
    {
        parentResourceId = _resource.ParentResourceAccount;
        resourceTable = WPPResourceTable::find(_resource.ResourceAccount);

        if (_newValue == NoYes::Yes)
        {
            selectedResourceIds.add(_resource.ResourceAccount);
            _resource.AssignObject = _resource.AssignObjectAllowed && (!resourceIdAssigned || resourceIdAssigned == _resource.ResourceAccount);
            ret = NoYes::Yes;
        }
        else
        {
            selectedResourceIds.remove(_resource.ResourceAccount);
            _resource.AssignObject = NoYes::No;
            ret = NoYes::No;
        }

        _resource.update();

        resourceIdAssigned = TmpAssignTask.AssignObject ? TmpAssignTask.ResourceAccount : resourceIdAssigned;

        if (resourceTable.IsIndividualResource)
        {
            if (_resource.ParentResourceAccount)
            {
                select forUpdate firstOnly TmpAssignTask
                where TmpAssignTask.ResourceAccount == parentResourceId;

                if (_newValue == NoYes::Yes)
                {
                    selectedResourceIds.add(TmpAssignTask.ResourceAccount);
                }
                else
                {
                    // Only uncheck the parent when no other child is checked
                    if (this.mustParentUnchecked(parentResourceId))
                    {
                        selectedResourceIds.remove(TmpAssignTask.ResourceAccount);
                        TmpAssignTask.AssignObject = NoYes::No;
                    }
                }

                TmpAssignTask.update();
            }
        }
        else
        {
            while select forUpdate TmpAssignTask
            where TmpAssignTask.ParentResourceAccount == resourceTable.ResourceId
            {
                if (_newValue == NoYes::Yes)
                {
                    selectedResourceIds.add(TmpAssignTask.ResourceAccount);
                }
                else
                {
                    selectedResourceIds.remove(TmpAssignTask.ResourceAccount);
                    TmpAssignTask.AssignObject = NoYes::No;
                }

                TmpAssignTask.update();
            }
        }
    }
    else
    {
        ret = selectedResourceIds.in(_resource.ResourceAccount);
    }

    return ret;
}

public void repopulate()
{
    tmpData.populate(PlanningStartDateTime.dateTimeValue(),
                     PlanningEndDateTime.dateTimeValue(),
                     element.args().parm(),
                     capabilitiesFilter.checked(),
                     callerWorkOrderLine,
                     spbSettings);

    TmpAssignTask.linkPhysicalTableInstance(tmpData.getTmpData());
    TmpAssignTask_ds.executeQuery();
}

