public void init()
{
    QueryBuildDataSource    qbdsSalesLine;
    SalesLine               salesLineLinked;
    WPPWorkOrderTable       workOrderTableLinked;

    super();

    select firstOnly RecId from salesLineLinked
        exists join workOrderTableLinked
        where workOrderTableLinked.OrderTableId == salesLineLinked.TableId
        &&    workOrderTableLinked.OrderRecId   == salesLineLinked.RecId
        &&    workOrderTableLinked.WorkOrderId  == callerWorkOrderLine.WorkOrderId;

    qbdsSalesLine = this.query().dataSourceTable(tableNum(SalesLine));

    if (salesLineLinked.RecId)
    {
        qbdsSalesLine.addRange(fieldNum(SalesLine, DataAreaId)).value(strFmt('(( %1 == %3 ) || (%2 == %3))',
                                                                              fieldStr(SalesLine, RecId),
                                                                              fieldStr(SalesLine, RORParentLineRecId),
                                                                              salesLineLinked.RecId));
    }
    else
    {
        qbdsSalesLine.addRange(fieldNum(SalesLine, RecId)).value(SysQuery::valueEmptyString());
    }
}

