public int sort(SortOrder _sortDirection)
{
    int ret;

    // Only allow sorting when planning by resource because while planning on
    // resource group also the resources are shown and the grouping of resources messed up
    if (spbSettings.PlanBy == WrkCtrGroupWrkCtr::WrkCtr)
    {
        ret = super(_sortDirection);
    }

    return ret;
}

