[Form]
public class WPPDirOrganizationLookup extends FormRun
{
    Object              callerDataSource;
    CustVendACType      custVendACType;
    boolean             canSelect;
    WPPWorkOrderTable   callerRecord;

}

public void closeOk()
{
    if (!canSelect)
    {
        return;
    }

    if (callerDataSource)
    {
        callerDataSource.refresh();
    }

    super();
}

public void closeSelectRecord(Common _selectedRecord)
{
    if (!canSelect)
    {
        return;
    }

    super(_selectedRecord);
}

[SuppressBPWarningAttribute("BPCheckInvalidInitFormMethodWarning", "Validation of caller must be performed before invoking super()")]
public void init()
{
    Args args = element.args();
    Object caller = args.caller();

    if (!args || !caller)
    {
        throw error("@SYS22539");
    }

    if (args.parmEnumType() == enumNum(CustVendACType))
    {
        custVendACType = args.parmEnum();
    }

    TableId callerDataSet = args.dataset(); 

    if (callerDataSet)
    {
        callerDataSource = FormDataUtil::getFormDataSource(args.record());

        if (callerDataSource)
        {
            callerRecord = callerDataSource.cursor();
        }
        else
        {
            //lookups using DirUtility::namelookup will not have datasource
            callerRecord = args.record();
        }
    }

    super(); 

    element.selectRecordModeSelectedRecord(DirPartyTable);
}

void run()
{
    FormControl callerControl = element.selectTarget();
    str         filterTableStr = tableStr(DirPartyTable);
    str         filterFieldStr = fieldStr(DirPartyTable, PartyNumber);

    if (callerControl is FormReferenceGroupControl)
    {
        FormReferenceGroupControl referenceControl = callerControl;

        super();

        FilterValue filterValue = referenceControl.filterValue(AbsoluteFieldBinding::construct(filterFieldStr, filterTableStr));

        if (filterValue)
        {
            str filterStr = filterValue.value();

            if (filterStr)
            {
                this.selectMode(DirPartyTable_PartyNumber);
                DirPartyTable_PartyNumber.setFocus();
                dirPartyTable_ds.research();

                DirPartyTable partyRecord = DirPartyTable::findByName(filterStr);

                if (partyRecord)
                {
                    element.args().lookupRecord(partyRecord);
                    dirPartyTable_DS.research(true);
                }
            }
        }
    }
    else
    {
        throw error(Error::wrongUseOfFunction(funcname()));
    }

    canSelect = false;
}

public void init()
{
    super();

    // Add fields exclusively. DS DirPartyTable has Only Fetch Active Set as true. So , we need to add the below fields to be used by DS that joins to DirPartyTable
    dirPartyTable_ds.addFieldToSelectionList(fieldNum(DirPartyTable, RecId));
    dirPartyTable_ds.addFieldToSelectionList(fieldNum(DirPartyTable, PartyNumber));

    QueryBuildDataSource qbdsDirPartyTable = this.queryBuildDataSource();
    qbdsDirPartyTable.clearDynalinks();
    qbdsDirPartyTable.clearLinks();
    qbdsDirPartyTable.clearRanges();
}

public void init()
{
    super();

    QueryBuildDataSource qbdsOrgTypeCustVendView = this.query().dataSourceName(this.name());

    if (custVendACType == CustVendACType::Cust)
    {
        qbdsOrgTypeCustVendView.addRange(fieldNum(WPPOrganizationTypeCustVendView, CustAccount)).value(SysQuery::valueNotEmptyString());
        qbdsOrgTypeCustVendView.addRange(fieldNum(WPPOrganizationTypeCustVendView, CustCompany)).value(SysQueryRangeUtil::currentCompany());

        WPPOrganizationTypeCustVendView_ds.object(fieldNum(WPPOrganizationTypeCustVendView, VendAccount)).visible(false);
    }
    else
    {
        qbdsOrgTypeCustVendView.addRange(fieldNum(WPPOrganizationTypeCustVendView, VendAccount)).value(SysQuery::valueNotEmptyString());
        qbdsOrgTypeCustVendView.addRange(fieldNum(WPPOrganizationTypeCustVendView, VendCompany)).value(SysQueryRangeUtil::currentCompany());

        WPPOrganizationTypeCustVendView_ds.object(fieldNum(WPPOrganizationTypeCustVendView, CustAccount)).visible(false);
    }
}

void clicked()
{
    canSelect = true;
    super();

    element.closeSelectRecord(DirPartyTable);
}
