public class WPPResourceTable extends FormRun
{
    DNRDateEffectivenessFormController  dateEffectivenessFormControllerResources;
    DateEffectivenessViewState          dateEffectivenessViewStateResources;

    DNRDateEffectivenessFormController  dateEffectivenessFormControllerResourceGrp;
    DateEffectivenessViewState          dateEffectivenessViewStateResourceGrp;

    DNRDateEffectivenessFormController  dateEffectivenessFormControllerCalendar;
    DateEffectivenessViewState          dateEffectivenessViewStateCalendar;

    DateEffectivenessFormController     dateEffectivenessFormControllerCapability;
    DateEffectivenessViewState          dateEffectivenessViewStateCapability;
    WPPResourceGroup                    displayMode;
    QueryBuildRange                     qbrResourceId;


    #define.CurrentVersion(1)
    #localmacro.CurrentList
        dateEffectivenessViewStateResourceGrp,
        dateEffectivenessViewStateResources,
        dateEffectivenessViewStateCalendar,
        dateEffectivenessViewStateCapability
    #endmacro


}

public void close()
{
    super();

    dateEffectivenessViewStateResourceGrp = dateEffectivenessFormControllerResourceGrp.viewState();
    dateEffectivenessViewStateResources   = dateEffectivenessFormControllerResources.viewState();
    dateEffectivenessViewStateCapability  = dateEffectivenessFormControllerCapability.viewState();
    dateEffectivenessViewStateCalendar    = dateEffectivenessFormControllerCalendar.viewState();

    xSysLastValue::saveLast(this);
}

public void init()
{
    if(element.args().parmEnumType() == enumNum(WPPResourceGroup))
    {
        displayMode = element.args().parmEnum();
    }
    else
    {
        throw error("@SYS22539");
    }

    super();

    dateEffectivenessFormControllerResourceGrp = DNRDateEffectivenessFormController::newFormControls(ResourceGroup_ds,
                                                                                                     ViewModeTextResourceGroup,
                                                                                                     ButtonViewAllResourceGroup,
                                                                                                     ButtonViewCurrentResourceGroup,
                                                                                                     ButtonDeleteResourceGroup,
                                                                                                     ButtonEditResourceGroup);

    dateEffectivenessFormControllerResources   = DNRDateEffectivenessFormController::newFormControls(ResourceGroupResource_ds,
                                                                                                     ViewModeTextResources,
                                                                                                     ButtonViewAllResources,
                                                                                                     ButtonViewCurrentResources,
                                                                                                     ButtonDeleteResource,
                                                                                                     ButtonEditResource);

    dateEffectivenessFormControllerCapability  = DateEffectivenessFormController::newFormControls(ResourceCapability_ds,
                                                                                                  ViewModeTextCapability,
                                                                                                  ButtonViewAllCapability,
                                                                                                  ButtonViewCurrentCapability,
                                                                                                  DeleteCapability,
                                                                                                  ButtonEditCapability);

    dateEffectivenessFormControllerCalendar    = DNRDateEffectivenessFormController::newFormControls(ResourceCalendarResource_ds,
                                                                                                     ViewModeTextCalendar,
                                                                                                     ButtonViewAllCalendars,
                                                                                                     ButtonViewCurrentCalendars,
                                                                                                     DeleteCalendar,
                                                                                                     ButtonEditCalendar);

    xSysLastValue::getLast(this);

    dateEffectivenessFormControllerResourceGrp.setViewState(dateEffectivenessViewStateResourceGrp);
    dateEffectivenessFormControllerResources.setViewState(dateEffectivenessViewStateResources);
    dateEffectivenessFormControllerCapability.setViewState(dateEffectivenessViewStateCapability);
    dateEffectivenessFormControllerCalendar.setViewState(dateEffectivenessViewStateCalendar);
    FINDimValueController::setEditableDimensionSet(tableNum(WPPResourceTable), DimensionEntryControl);

    this.updateDesign();
}

