public container pack()
{
    return [#CurrentVersion,#CurrentList];
}

public boolean unpack(container packedClass)
{
    int version     = RunBase::getVersion(packedClass);
    boolean result;

    switch (version)
    {
        case #CurrentVersion:
            [version,#CurrentList] = packedClass;
            result = true;
        default :
            result = false;
    }

    return result;
}

public void updateDesign()
{
    if(displayMode == WPPResourceGroup ::Resource)
    {
        TabPageCapabilities.visible(true);
        TabPageResourceGroup.visible(true);
        WPPResourceTable_RelationAccount.visible(true);
        ResourceTable_ResourceCategory.visible(true);
        element.design().caption("@SYS10369");
        WPPResourceTable_AutoGenerateJourTrans.visible(true);
        displayRelationAccountName.visible(true);
        WPPResourceTable_ResourceTypeId.visible(true);
        DropDialogButtonResourceGroupAdd.visible(true);
        ResourceCapabilityAdd.visible(true);
        DropDialogButtonCopy.visible(true);
    }
    else
    {
        TabPageResources.visible(true);
        element.design().caption("@SYS122587");
    }
}

void researchAndFind(WPPResourceId _resourceId)
{
    ResourceTable_ds.research();
    ResourceTable_ds.positionToRecord(WPPResourceTable::find(_resourceId));
}

public void init()
{
    QueryBuildRange qbrIsIndividualResource;

    super();

    qbrIsIndividualResource = this.queryBuildDataSource().addRange(fieldNum(WPPResourceTable, IsIndividualResource));

    if(displayMode == WPPResourceGroup ::Resource)
    {
        qbrIsIndividualResource.value(SysQuery::value(NoYes::Yes));
    }
    else
    {
        qbrIsIndividualResource.value(SysQuery::value(NoYes::No));
    }
}

public void initValue()
{
    super();

    if(displayMode == WPPResourceGroup::Resource)
    {
        ResourceTable.IsIndividualResource =  NoYes::Yes;
        ResourceTable.ResourceCategory = WPPResourceCategory::BusinessObject;

    }
    else
    {
        ResourceTable.IsIndividualResource = NoYes::No;
        ResourceTable.ResourceCategory = WPPResourceCategory::ResourceGroup;
    }
}

public int active()
{
    int ret;

    ret = super();

    if(displayMode == WPPResourceGroup::Resource)
    {
        ResourceGroup_ds.executeQuery();
    }
    else
    {
        ResourceGroupResource_ds.executeQuery();
    }

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    if(ResourceTable.ResourceCategory == WPPResourceCategory::BusinessObject)
    {
        WPPResourceTable::lookupBOBObjectId(_formControl);
    }
    else
    {
        super(_formControl, _filterStr);
    }
}

public boolean validateDelete()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        // Deletion is done by the controller
        dateEffectivenessFormControllerResources.delete();
    }

    // Return false always to not also have the form try to delete the record.
    // This means that the Deleting and Deleted events will never be raised
    return false;
}

