public void init()
{
    dataClassUnplanned      = new WPPServicePlanboardData_Unplanned();
    userPreferredTimeZone   = DateTimeUtil::getUserPreferredTimeZone();

    super();

    xSysLastValue::getLast(this);

    if (settingsPacked)
    {
        settings = con2Buf(settingsPacked);
    }

    // initialize form layout
    element.updateOrientation();

    element.initPlanboardUnplanned();
    element.initPlanboardPlanned();
}

public void initParmDefault()
{
    settings.initValue();
}

public void initPlanboardPlanned()
{
    #TimeConstants

    formClassPlanned = new WPPServicePlanboardForm(element);
    dataClassPlanned = new WPPServicePlanboardData_Planned(HPBPlanned);

    StartDateTime startDateTime = DateTimeUtil::newDateTime(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()), 0);
    EndDateTime endDateTime = DateTimeUtil::addSeconds(DateTimeUtil::addDays(startDateTime, 1), timeMax());

    FromDateTime.dateTimeValue(startDateTime);
    ToDateTime.dateTimeValue(endDateTime);

    this.setTimeUnit();

    HPBPlanned.parmRoWItemWidth(settings.RowItemWidth);
}

private void setTimeUnit()
{
    if (settings.TimeUnits)
    {
        TimeUnit.value(settings.TimeUnits);
    }
    else
    {
        TimeUnit.value(#MinutesPerHour);
        TimeUnit.modified();
    }
}

public void initPlanboardUnplanned()
{
    formClassUnplanned  = new WPPServicePlanboardForm(element);
}

