// called from \Classes\WPPServicePlanboardForm
public void refreshApply(boolean _doUnplanned, boolean _doPlanned)
{
    xSysLastValue::getLast(this);

    if (settingsPacked)
    {
        settings = con2Buf(settingsPacked);
    }

    boolean validateDates()
    {
        #TimeConstants
        boolean ret = true;

        if (!FromDateTime.dateTimeValue())
        {
            ret = checkFailed(strFmt("@SYS26332", "@DNR6468"));
        }

        if (ret && !ToDateTime.dateTimeValue())
        {
            ret = checkFailed(strFmt("@SYS26332", "@DNR6469"));
        }

        if (ret && DateTimeUtil::getDifference(ToDateTime.dateTimeValue(), FromDateTime.dateTimeValue()) < (TimeUnit.value() * #secondsPerMinute(60)))
        {
            ret = checkFailed(strFmt("@SYS50105", "@DNR6468", "@DNR6469"));
        }

        return ret;
    }

    if (_doUnplanned)
    {
        //TabPageUnplanned.visible(settings.DisplayMode == WPPPlanboardDisplayMode::Grid);
        dataClassUnplanned.populateTmpData(settings);
        TmpDataUnplanned.linkPhysicalTableInstance(dataClassUnplanned.getTmpData());
        TmpDataUnplanned_ds.research();
    }

    if (_doPlanned && validateDates())
    {
        HPBPlanned.parmPeriodMinHeight(settings.CellHeightPlanned);
        HPBPlanned.parmStartDate(FromDateTime.dateTimeValue());
        HPBPlanned.parmEndDate(ToDateTime.dateTimeValue());
        HPBPlanned.parmTimeScale(TimeUnit.value());

        dataClassPlanned.parmCallerWorkOrderLineRecId(formClassUnplanned.parmCallerWorkOrderLineRecId() ?
                                                      formClassUnplanned.parmCallerWorkOrderLineRecId() :
                                                      formClassPlanned.parmCallerWorkOrderLineRecId());

        dataClassPlanned.parmCallerFromDateTime(DateTimeUtil::removeTimeZoneOffset(FromDateTime.dateTimeValue(), UserPreferredTimeZone));
        dataClassPlanned.parmCallerToDateTime(DateTimeUtil::removeTimeZoneOffset(ToDateTime.dateTimeValue(), UserPreferredTimeZone));
        dataClassPlanned.populateTmpData(settings);
        dataClassPlanned.populatePlanboard(settings);
    }
}

public void refreshEntity(HiGHPlanBoardRowItem _rowItem, WrkCtrId _resourceId)
{
    Map workingTimesMap;

    workingTimesMap = dataClassPlanned.getEntityAvailability(_resourceId, settings.RegionCalendarId);

    dataClassPlanned.setEntityAvailability(_rowItem, workingTimesMap);

    HPBPlanned.refresh();
}

public void refreshApplySpecific(Set _resourceIdSet, RefRecId _workOrderLineRecId, boolean refreshUnplanned)
{
    dataClassPlanned.setResourceIdSet(_resourceIdSet);
    dataClassUnplanned.setWorkOrderLineRecId(_workOrderLineRecId);

    this.refreshApply(refreshUnplanned, true);
}

