public void run()
{
    super();

    // initial population
    unplannedSettingsChanged = true;
    plannedSettingsChanged = true;
    element.refreshApply(true, true);
}

public void showAdditionalNotes(HeadingLine _headingLine)
{
    Args args = new Args(element);

    if (_headingLine == HeadingLine::Heading)
    {
        WPPWorkOrderTable workOrderTable;

        select firstonly workOrderTable
        where workOrderTable.WorkOrderId == TmpDataUnplanned.WPPWorkOrderId;

        args.record(workOrderTable);

        new MenuFunction(menuItemDisplayStr(DNRAdditionalNoteTable), MenuItemType::Display).run(args);
    }
    else
    {
        WPPWorkOrderLine workOrderLine;

        select firstonly workOrderLine
        where workOrderLine.RecId == TmpDataUnplanned.WPPWorkOrderLineRecId;

        args.record(workOrderLine);

        new MenuFunction(menuItemDisplayStr(DNRAdditionalNoteTable_Line), MenuItemType::Display).run(args);
    }
}

public int task(int _taskId)
{
    #Task
    int ret = super(_taskId);

    switch(_taskId)
    {
        case 4614: // F6
            element.refreshApply(true, false); // refresh unplanned tasks
            break;

        case 1321: // F9
            element.refreshApply(false, true); // refresh planned tasks
            break;

        case 4615: // F7
            element.refreshApply(true, true); // refresh all
            break;

        default:
            //info (int2Str(_taskId));
            break;
    }

    return ret;
}

public boolean unpack(container _packedClass)
{
    int version = RunBase::getVersion(_packedClass);

    switch(version)
    {
        case #CurrentVersion:
            [ version, #CurrentList ] = _packedClass;
            return true;

        default:
            return false;
    }
}

public void updateOrientation()
{
    if (settings.OrientationMode == WPPPlanboardOrientationMode::Vertical)
    {
        Splitter.style(GroupStyle::SplitterVerticalContainer);

        GroupPlanning.columns(3);
        UnplannedTabControl.heightMode(FormHeight::SizeToAvailable);
        UnplannedTabControl.widthMode(FormWidth::Manual);
        UnplannedTabControl.width(850);
        GridUnplanned.heightMode(FormHeight::SizeToAvailable);
        GridUnplanned.height(750);

        //HPBPlanned.height(750);
        //HPBPlanned.width(900);
    }
    else
    {
        Splitter.style(GroupStyle::SplitterHorizontalContainer);

        GroupPlanning.columns(1);
        UnplannedTabControl.heightMode(FormHeight::SizeToAvailable);
        UnplannedTabControl.widthMode(FormWidth::SizeToAvailable);
        GridUnplanned.heightMode(FormHeight::SizeToAvailable);
        HPBPlanned.widthMode(FormWidth::SizeToAvailable);
        HPBPlanned.heightMode(FormHeight::SizeToAvailable);

        //HPBPlanned.height(400);
        //HPBPlanned.width(1800);
    }


}

