private container getPackedImageResource(WPPTmpServicePlanboardData _tmpDataUnplanned, ABC _abc)
{
    ImageResourceName imgResName;

    container conGridWarningIcons = formClassUnplanned.getGridWarningIcons(_tmpDataUnplanned);

    if (conLen(conGridWarningIcons))
    {
        switch(_abc)
        {
            case ABC::A:
                imgResName = conPeek(conGridWarningIcons, 1);
                break;

            case ABC::B:
                imgResName = conPeek(conGridWarningIcons, 2);
                break;

            case ABC::C:
                imgResName = conPeek(conGridWarningIcons, 3);
                break;
        }
    }

    return imgResName ? ImageReference::constructForAotResource(imgResName).pack() : conNull();
}

public void init()
{
    super();

    // Our instance of WPPServicePlanboardData_Unplanned hasn't been initialized at
    // this point, because its constructor requires a planboard control instance
    // (which isn't ready yet). So in order to establish the link between this
    // data source and the TempDb table, we need to instantiate the temporary table,
    // insert at least one record, and then connect it to the data source here.
    TmpDataUnplanned.linkPhysicalTableInstance(dataClassUnplanned.getEmptyTmpTable());
}

