public void init()
{
    QueryBuildDataSource qbdsAdditionalNoteTable;
    QueryBuildDataSource qbdsWorkOrderTable;

    super();

    qbdsAdditionalNoteTable = this.queryBuildDataSource();

    qbdsWorkOrderTable = qbdsAdditionalNoteTable.addDataSource(tableNum(WPPWorkOrderTable));
    qbdsWorkOrderTable.relations(true);
    qbdsWorkOrderTable.joinMode(JoinMode::ExistsJoin);
    qbdsWorkOrderTable.addDynalink(fieldNum(WPPWorkOrderTable, WorkOrderId), TmpDataUnplanned, fieldNum(WPPTmpServicePlanboardData, WPPWorKOrderId));
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.queryBuildDataSource();
    qbds.addDynalink(fieldNum(DNRAdditionalNoteTable, RefRecId), TmpDataUnplanned, fieldNum(WPPTmpServicePlanboardData, WPPWorkOrderLineRecId));
    qbds.addRange(fieldNum(DNRAdditionalNoteTable, RefTableId)).value(SysQuery::value(tableNum(WPPWorkOrderLine)));
}

