public void close()
{
    Object callerFormRunSPB;
    ;

    super();

    if(callerIsSPB && mustRefreshSPB)
    {
        callerFormRunSPB = element.args().caller();

        callerFormRunSPB.refreshApply(true, true);
    }
}

